plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

' Deployment diagram for Node.js Chat Application
node "Node.js Chat Server" as chatServer <<JavaScript, Express, Socket.io>> {
}

node "Docker Container" as dockerContainer <<Docker>> {
}

node "CI/CD Pipeline" as cicd <<Jenkins, CodeBuild>> {
}

cloud "AWS Cloud" as aws {
  node "Terraform Managed Infrastructure" as terraform <<Terraform>> {
  }
}

' Relationships
chatServer -down-> dockerContainer : "Containerized via Docker"
dockerContainer -down-> cicd : "Built & Deployed by CI/CD"
cicd -down-> terraform : "Deploys Infrastructure"
terraform -down-> aws : "Provisions AWS Resources"

@enduml
