plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

' AWS Cloud
node "AWS Cloud" as aws_cloud {
  node "EKS Cluster\n(Terraform managed)" as eks {
    node "Kubernetes Deployment\nrange-slider-deployment" as k8s_deploy {
      node "Docker Container\nnginx:alpine" as nginx_container {
        artifact "Static Web App\n(HTML/CSS/JS)" as static_app
      }
    }
    node "Kubernetes Service\nrange-slider-service\n(LoadBalancer, AWS ALB)" as k8s_svc
  }
}

' CI/CD
node "CI/CD Pipeline" as cicd {
  artifact "GitHub Actions" as gha
  artifact "Jenkins" as jenkins
}

' Relationships
cicd --> nginx_container : Build & Push Docker Image
cicd --> eks : Deploy via manifests
k8s_deploy --> nginx_container : Runs
nginx_container --> static_app : Serves static files
k8s_deploy --> k8s_svc : Exposed by
k8s_svc --> aws_cloud : Exposes to Internet (via ALB)

@enduml
