plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

' Deployment diagram for a static web application on Kubernetes (EKS) with Nginx
node "User" as user

node "AWS EKS Cluster" as eks {
  node "Kubernetes Deployment\n(notes-keeper)" as k8s_deploy {
    node "Docker Container\n(Nginx, Alpine)" as nginx {
      artifact "Static Web App\n(HTML, JS, CSS)" as static_app
    }
  }
  node "Kubernetes Service\n(LoadBalancer, AWS ALB)" as k8s_svc
}

cloud "Docker Container Registry\n(GitHub Container Registry / Docker Hub)" as registry

user --> k8s_svc : HTTP/HTTPS
k8s_svc --> k8s_deploy : Forwards traffic
k8s_deploy --> nginx : Pod runs container
nginx --> static_app : Serves static files
nginx ..> registry : Pulls image

@enduml
