terraform {
  required_providers {
    aws = {
      source  = "hashicorp/aws"
      version = "~> 6.27.0"
    }
  }
  required_version = ">= 1.3.0"
}

provider "aws" {
  region = var.aws_region
}

variable "aws_region" {
  description = "AWS region to deploy resources in."
  type        = string
  default     = "us-east-1"
}

variable "vpc_cidr" {
  description = "CIDR block for the VPC."
  type        = string
  default     = "10.0.0.0/16"
}

variable "public_subnet_cidrs" {
  description = "List of public subnet CIDR blocks."
  type        = list(string)
  default     = ["10.0.1.0/24"]
}

variable "private_subnet_cidrs" {
  description = "List of private subnet CIDR blocks."
  type        = list(string)
  default     = ["10.0.2.0/24"]
}

variable "influxdb_instance_type" {
  description = "EC2 instance type for InfluxDB."
  type        = string
  default     = "t3.medium"
}

variable "grafana_instance_type" {
  description = "EC2 instance type for Grafana."
  type        = string
  default     = "t3.small"
}

variable "lambda_image_uri" {
  description = "ECR image URI for the Lambda function."
  type        = string
}

variable "tuya_secret_arn" {
  description = "ARN of the Secrets Manager secret for Tuya API credentials."
  type        = string
}

variable "influxdb_secret_arn" {
  description = "ARN of the Secrets Manager secret for InfluxDB credentials."
  type        = string
}

output "vpc_id" {
  value = aws_vpc.main.id
}

output "public_subnet_ids" {
  value = aws_subnet.public[*].id
}

output "private_subnet_ids" {
  value = aws_subnet.private[*].id
}

output "influxdb_public_ip" {
  value = aws_instance.influxdb.public_ip
  description = "Public IP of the InfluxDB EC2 instance (if needed for debugging)."
}

output "grafana_public_ip" {
  value = aws_instance.grafana.public_ip
  description = "Public IP of the Grafana EC2 instance (if exposed)."
}

output "lambda_function_name" {
  value = aws_lambda_function.main.function_name
}

resource "aws_vpc" "main" {
  cidr_block           = var.vpc_cidr
  enable_dns_support   = true
  enable_dns_hostnames = true
  tags = {
    Name = "main-vpc"
  }
}

resource "aws_internet_gateway" "main" {
  vpc_id = aws_vpc.main.id
  tags = {
    Name = "main-igw"
  }
}

resource "aws_subnet" "public" {
  count                   = length(var.public_subnet_cidrs)
  vpc_id                  = aws_vpc.main.id
  cidr_block              = var.public_subnet_cidrs[count.index]
  map_public_ip_on_launch = true
  availability_zone       = data.aws_availability_zones.available.names[count.index]
  tags = {
    Name = "public-subnet-${count.index + 1}"
  }
}

resource "aws_subnet" "private" {
  count             = length(var.private_subnet_cidrs)
  vpc_id            = aws_vpc.main.id
  cidr_block        = var.private_subnet_cidrs[count.index]
  availability_zone = data.aws_availability_zones.available.names[count.index]
  tags = {
    Name = "private-subnet-${count.index + 1}"
  }
}

resource "aws_nat_gateway" "main" {
  allocation_id = aws_eip.nat.id
  subnet_id     = aws_subnet.public[0].id
  tags = {
    Name = "main-nat-gw"
  }
}

resource "aws_eip" "nat" {
  domain = "vpc"
}

resource "aws_route_table" "public" {
  vpc_id = aws_vpc.main.id
  tags = {
    Name = "public-rt"
  }
}

resource "aws_route" "public_internet_access" {
  route_table_id         = aws_route_table.public.id
  destination_cidr_block = "0.0.0.0/0"
  gateway_id             = aws_internet_gateway.main.id
}

resource "aws_route_table_association" "public" {
  count          = length(var.public_subnet_cidrs)
  subnet_id      = aws_subnet.public[count.index].id
  route_table_id = aws_route_table.public.id
}

resource "aws_route_table" "private" {
  vpc_id = aws_vpc.main.id
  tags = {
    Name = "private-rt"
  }
}

resource "aws_route" "private_nat_gateway" {
  route_table_id         = aws_route_table.private.id
  destination_cidr_block = "0.0.0.0/0"
  nat_gateway_id         = aws_nat_gateway.main.id
}

resource "aws_route_table_association" "private" {
  count          = length(var.private_subnet_cidrs)
  subnet_id      = aws_subnet.private[count.index].id
  route_table_id = aws_route_table.private.id
}

data "aws_availability_zones" "available" {}

resource "aws_security_group" "lambda" {
  name        = "lambda-sg"
  description = "Allow Lambda to access InfluxDB and internet"
  vpc_id      = aws_vpc.main.id

  egress {
    from_port   = 0
    to_port     = 0
    protocol    = "-1"
    cidr_blocks = ["0.0.0.0/0"]
  }

  tags = {
    Name = "lambda-sg"
  }
}

resource "aws_security_group" "influxdb" {
  name        = "influxdb-sg"
  description = "Allow access to InfluxDB from Lambda and Grafana"
  vpc_id      = aws_vpc.main.id

  ingress {
    from_port   = 8086
    to_port     = 8086
    protocol    = "tcp"
    security_groups = [aws_security_group.lambda.id, aws_security_group.grafana.id]
  }

  egress {
    from_port   = 0
    to_port     = 0
    protocol    = "-1"
    cidr_blocks = ["0.0.0.0/0"]
  }

  tags = {
    Name = "influxdb-sg"
  }
}

resource "aws_security_group" "grafana" {
  name        = "grafana-sg"
  description = "Allow HTTP/HTTPS access to Grafana and access to InfluxDB"
  vpc_id      = aws_vpc.main.id

  ingress {
    from_port   = 3000
    to_port     = 3000
    protocol    = "tcp"
    cidr_blocks = ["0.0.0.0/0"]
  }

  egress {
    from_port   = 0
    to_port     = 0
    protocol    = "-1"
    cidr_blocks = ["0.0.0.0/0"]
  }

  tags = {
    Name = "grafana-sg"
  }
}

resource "aws_iam_role" "lambda_exec" {
  name = "lambda-exec-role"
  assume_role_policy = jsonencode({
    Version = "2012-10-17"
    Statement = [{
      Action = "sts:AssumeRole"
      Effect = "Allow"
      Principal = {
        Service = "lambda.amazonaws.com"
      }
    }]
  })
}

resource "aws_iam_role_policy_attachment" "lambda_vpc" {
  role       = aws_iam_role.lambda_exec.name
  policy_arn = "arn:aws:iam::aws:policy/service-role/AWSLambdaVPCAccessExecutionRole"
}

resource "aws_iam_role_policy_attachment" "lambda_logs" {
  role       = aws_iam_role.lambda_exec.name
  policy_arn = "arn:aws:iam::aws:policy/service-role/AWSLambdaBasicExecutionRole"
}

resource "aws_iam_policy" "lambda_secrets" {
  name        = "lambda-secrets-policy"
  description = "Allow Lambda to access Secrets Manager for Tuya and InfluxDB credentials"
  policy      = jsonencode({
    Version = "2012-10-17"
    Statement = [
      {
        Effect = "Allow"
        Action = [
          "secretsmanager:GetSecretValue"
        ]
        Resource = [var.tuya_secret_arn, var.influxdb_secret_arn]
      }
    ]
  })
}

resource "aws_iam_policy_attachment" "lambda_secrets_attach" {
  name       = "lambda-secrets-attach"
  roles      = [aws_iam_role.lambda_exec.name]
  policy_arn = aws_iam_policy.lambda_secrets.arn
}

resource "aws_lambda_function" "main" {
  function_name = "tuya-influxdb-collector"
  image_uri     = var.lambda_image_uri
  package_type  = "Image"
  role          = aws_iam_role.lambda_exec.arn
  timeout       = 60
  memory_size   = 1024

  vpc_config {
    subnet_ids         = aws_subnet.private[*].id
    security_group_ids = [aws_security_group.lambda.id]
  }

  environment {
    variables = {
      TUYA_SECRET_ARN    = var.tuya_secret_arn
      INFLUXDB_SECRET_ARN = var.influxdb_secret_arn
    }
  }

  depends_on = [aws_iam_role_policy_attachment.lambda_vpc, aws_iam_role_policy_attachment.lambda_logs, aws_iam_policy_attachment.lambda_secrets_attach]
}

resource "aws_secretsmanager_secret" "tuya" {
  name = "tuya-api-credentials"
  description = "Tuya API credentials for Lambda"
}

resource "aws_secretsmanager_secret" "influxdb" {
  name = "influxdb-credentials"
  description = "InfluxDB credentials for Lambda"
}
