plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

title Minecraft (Paper) + BungeeCord + RCON + Backups (Docker Compose)

skinparam shadowing false
skinparam componentStyle rectangle

node "Host Machine\n(Docker Engine + Docker Compose)" as host {

  frame "Docker Compose Network" as net {

    node "lobby" as lobby {
      artifact "Minecraft Server\nPaper (itzg/minecraft-server)\nPorts: 25565 (game), 25575 (RCON)" as paper
      folder "Persistent Data\n./data/lobby" as data_lobby
      folder "Plugins\n./plugins/lobby" as plugins_lobby
    }

    node "proxy" as proxy {
      artifact "Minecraft Proxy\nBungeeCord/Waterfall (itzg/bungeecord)" as bungee
      folder "Persistent Data\n./data/proxy" as data_proxy
      folder "Plugins\n./plugins/proxy" as plugins_proxy
    }

    node "rcon" as rcon {
      artifact "RCON Web Admin\n(itzg/rcon)\nPorts: 4326/4327" as rconweb
      folder "Persistent Data\n./data/rcon" as data_rcon
    }

    node "backup" as backup {
      artifact "Backup Service\n(itzg/mc-backup)\nPeriodic world backups" as mcbackup
      folder "Backups Output\n./mc-backups" as backups_dir
    }

  }
}

cloud "Players / Admins" as users

users --> bungee : "Minecraft client\nTCP 25565"
users --> rconweb : "Web UI\n4326/4327"

bungee --> paper : "Proxy upstream\nMinecraft protocol"
rconweb --> paper : "RCON commands\nTCP 25575"
mcbackup --> paper : "Read world data\n(via shared volume / container access)"

paper -- data_lobby
paper -- plugins_lobby
bungee -- data_proxy
bungee -- plugins_proxy
rconweb -- data_rcon
mcbackup -- backups_dir

@enduml
