plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

skinparam shadowing false
skinparam dpi 150

title High-level Deployment Architecture (Flask on Kubernetes with Jenkins CI/CD)

actor "Developer" as dev

node "Jenkins CI/CD\n(Groovy Jenkinsfile)" as jenkins {
  artifact "Pipeline\n- Build Docker image\n- Push to Registry\n- kubectl apply" as pipeline
}

cloud "Container Image Registry\n(Docker Hub)" as registry

node "Kubernetes Cluster\n(e.g., Minikube)" as k8s {
  node "Worker Node(s)\n(VM/Compute)" as worker {
    node "Pod: Flask Web App" as pod {
      artifact "Container: Python 3 + Flask 2.1.1\nListens on TCP/8080" as app
    }
  }
  node "Kubernetes Service\n(Exposes TCP/8080)" as svc
}

node "Local VM Provisioning\n(Vagrant/VirtualBox)" as vagrant {
  artifact "VM(s) for\nJenkins and/or K8s" as vms
}

dev --> jenkins : Commit/Trigger build
jenkins --> registry : docker push image
jenkins --> k8s : kubectl apply\n(Deployment/Service)
registry --> pod : image pull
svc --> pod : routes traffic

@enduml
