files.content:
terraform {
  required_version = ">= 1.5.0"

  required_providers {
    aws = {
      source  = "hashicorp/aws"
      version = "~> 5.0"
    }
  }
}

provider "aws" {
  region  = var.aws_region
  profile = var.aws_profile

  default_tags {
    tags = var.tags
  }
}

variable "aws_region" {
  description = "AWS region to deploy into."
  type        = string
  default     = "us-east-1"
}

variable "aws_profile" {
  description = "AWS CLI profile name to use (matches boto3.Session(profile_name=...))."
  type        = string
  default     = "user1001"
}

variable "bucket_name" {
  description = "Name of the S3 bucket used by the workshop scripts. Must be globally unique."
  type        = string
  default     = "s3-bucket-workshop-1001-pyday"
}

variable "images_prefix" {
  description = "S3 key prefix used by the scripts (e.g., imagenes/)."
  type        = string
  default     = "imagenes/"

  validation {
    condition     = endswith(var.images_prefix, "/")
    error_message = "images_prefix must end with '/'."
  }
}

variable "enable_versioning" {
  description = "Enable S3 versioning (recommended)."
  type        = bool
  default     = true
}

variable "create_iam_user" {
  description = "Whether to create an IAM user (legacy pattern). Prefer SSO/Identity Center in real environments."
  type        = bool
  default     = false
}

variable "iam_user_name" {
  description = "IAM user name to create if create_iam_user=true."
  type        = string
  default     = "user1001"
}

variable "tags" {
  description = "Tags applied to all supported resources."
  type        = map(string)
  default = {
    Project = "aws-boto3-workshop"
    Managed = "terraform"
  }
}

data "aws_caller_identity" "current" {}

data "aws_partition" "current" {}

data "aws_region" "current" {}

# --- S3 bucket for workshop uploads/downloads ---
resource "aws_s3_bucket" "workshop" {
  bucket = var.bucket_name
}

resource "aws_s3_bucket_public_access_block" "workshop" {
  bucket = aws_s3_bucket.workshop.id

  block_public_acls       = true
  block_public_policy     = true
  ignore_public_acls      = true
  restrict_public_buckets = true
}

resource "aws_s3_bucket_ownership_controls" "workshop" {
  bucket = aws_s3_bucket.workshop.id

  rule {
    object_ownership = "BucketOwnerEnforced"
  }
}

resource "aws_s3_bucket_versioning" "workshop" {
  bucket = aws_s3_bucket.workshop.id

  versioning_configuration {
    status = var.enable_versioning ? "Enabled" : "Suspended"
  }
}

resource "aws_s3_bucket_server_side_encryption_configuration" "workshop" {
  bucket = aws_s3_bucket.workshop.id

  rule {
    apply_server_side_encryption_by_default {
      sse_algorithm = "AES256"
    }

    bucket_key_enabled = true
  }
}

resource "aws_s3_bucket_policy" "workshop" {
  bucket = aws_s3_bucket.workshop.id

  policy = jsonencode({
    Version = "2012-10-17"
    Statement = [
      # Enforce TLS
      {
        Sid       = "DenyInsecureTransport"
        Effect    = "Deny"
        Principal = "*"
        Action    = "s3:*"
        Resource = [
          aws_s3_bucket.workshop.arn,
          "${aws_s3_bucket.workshop.arn}/*",
        ]
        Condition = {
          Bool = {
            "aws:SecureTransport" = "false"
          }
        }
      },
    ]
  })

  depends_on = [aws_s3_bucket_public_access_block.workshop]
}

# --- Optional IAM user + least-privilege policy (legacy access keys pattern) ---
resource "aws_iam_user" "workshop" {
  count = var.create_iam_user ? 1 : 0
  name  = var.iam_user_name
}

data "aws_iam_policy_document" "workshop_cli" {
  statement {
    sid    = "S3ListBucket"
    effect = "Allow"

    actions = [
      "s3:ListBucket",
      "s3:GetBucketLocation",
    ]

    resources = [aws_s3_bucket.workshop.arn]

    condition {
      test     = "StringLike"
      variable = "s3:prefix"
      values = [
        var.images_prefix,
        "${var.images_prefix}*",
      ]
    }
  }

  statement {
    sid    = "S3ObjectRWWithinPrefix"
    effect = "Allow"

    actions = [
      "s3:GetObject",
      "s3:PutObject",
      "s3:DeleteObject",
      "s3:AbortMultipartUpload",
      "s3:ListBucketMultipartUploads",
      "s3:ListMultipartUploadParts",
    ]

    resources = ["${aws_s3_bucket.workshop.arn}/${var.images_prefix}*"]
  }

  statement {
    sid    = "EC2DescribeReadOnly"
    effect = "Allow"

    actions = [
      "ec2:DescribeRegions",
      "ec2:DescribeAvailabilityZones",
      "ec2:DescribeInstances",
      "ec2:DescribeTags",
    ]

    resources = ["*"]
  }
}

resource "aws_iam_policy" "workshop_cli" {
  count       = var.create_iam_user ? 1 : 0
  name_prefix = "workshop-cli-"
  description = "Least-privilege policy for aws_boto3_workshop scripts (S3 prefix + EC2 Describe*)."
  policy      = data.aws_iam_policy_document.workshop_cli.json
}

resource "aws_iam_user_policy_attachment" "workshop_cli" {
  count      = var.create_iam_user ? 1 : 0
  user       = aws_iam_user.workshop[0].name
  policy_arn = aws_iam_policy.workshop_cli[0].arn
}

output "bucket_name" {
  description = "S3 bucket name for workshop uploads/downloads."
  value       = aws_s3_bucket.workshop.bucket
}

output "bucket_arn" {
  description = "S3 bucket ARN."
  value       = aws_s3_bucket.workshop.arn
}

output "images_prefix" {
  description = "S3 prefix intended for images."
  value       = var.images_prefix
}

output "iam_user_name" {
  description = "IAM user name (if created)."
  value       = try(aws_iam_user.workshop[0].name, null)
}
