files.content:
terraform {
  required_version = ">= 1.6.0"

  required_providers {
    aws = {
      source  = "hashicorp/aws"
      version = "~> 5.0"
    }
    random = {
      source  = "hashicorp/random"
      version = "~> 3.6"
    }
  }
}

provider "aws" {
  region = var.aws_region

  default_tags {
    tags = var.tags
  }
}

variable "aws_region" {
  description = "AWS region to deploy into."
  type        = string
  default     = "us-east-1"
}

variable "name" {
  description = "Name prefix for resources."
  type        = string
  default     = "atlas-mysql"
}

variable "vpc_cidr" {
  description = "CIDR block for the VPC."
  type        = string
  default     = "10.20.0.0/16"
}

variable "az_count" {
  description = "Number of AZs to use (2 or 3)."
  type        = number
  default     = 2
  validation {
    condition     = var.az_count >= 2 && var.az_count <= 3
    error_message = "az_count must be 2 or 3."
  }
}

variable "db_name" {
  description = "Initial database name to create in RDS."
  type        = string
  default     = "app"
}

variable "db_username" {
  description = "Master username for the RDS instance."
  type        = string
  default     = "admin"
}

variable "db_instance_class" {
  description = "RDS instance class."
  type        = string
  default     = "db.t3.micro"
}

variable "db_allocated_storage" {
  description = "Allocated storage in GB."
  type        = number
  default     = 20
}

variable "db_max_allocated_storage" {
  description = "Max allocated storage in GB (autoscaling)."
  type        = number
  default     = 100
}

variable "db_backup_retention_period" {
  description = "Backup retention in days."
  type        = number
  default     = 7
}

variable "db_multi_az" {
  description = "Whether to enable Multi-AZ for the RDS instance."
  type        = bool
  default     = false
}

variable "db_deletion_protection" {
  description = "Enable deletion protection on the RDS instance."
  type        = bool
  default     = false
}

variable "db_publicly_accessible" {
  description = "Whether the DB should have a public endpoint. Keep false for private-only."
  type        = bool
  default     = false
}

variable "db_port" {
  description = "MySQL port."
  type        = number
  default     = 3306
}

variable "allowed_cidrs" {
  description = "CIDR blocks allowed to connect to the DB (only used if you set db_publicly_accessible=true)."
  type        = list(string)
  default     = []
}

variable "tags" {
  description = "Tags to apply to all resources."
  type        = map(string)
  default = {
    Project = "atlas-migrations"
  }
}

data "aws_availability_zones" "available" {
  state = "available"
}

locals {
  azs = slice(data.aws_availability_zones.available.names, 0, var.az_count)

  # /20 per subnet gives room for growth; requires vpc_cidr to be at least /16.
  public_subnet_cidrs  = [for i in range(var.az_count) : cidrsubnet(var.vpc_cidr, 4, i)]
  private_subnet_cidrs = [for i in range(var.az_count) : cidrsubnet(var.vpc_cidr, 4, i + 8)]
}

resource "aws_vpc" "this" {
  cidr_block           = var.vpc_cidr
  enable_dns_support   = true
  enable_dns_hostnames = true

  tags = {
    Name = "${var.name}-vpc"
  }
}

resource "aws_internet_gateway" "this" {
  vpc_id = aws_vpc.this.id

  tags = {
    Name = "${var.name}-igw"
  }
}

resource "aws_subnet" "public" {
  for_each = { for idx, az in local.azs : az => idx }

  vpc_id                  = aws_vpc.this.id
  availability_zone       = each.key
  cidr_block              = local.public_subnet_cidrs[each.value]
  map_public_ip_on_launch = true

  tags = {
    Name = "${var.name}-public-${each.key}"
    Tier = "public"
  }
}

resource "aws_subnet" "private" {
  for_each = { for idx, az in local.azs : az => idx }

  vpc_id                  = aws_vpc.this.id
  availability_zone       = each.key
  cidr_block              = local.private_subnet_cidrs[each.value]
  map_public_ip_on_launch = false

  tags = {
    Name = "${var.name}-private-${each.key}"
    Tier = "private"
  }
}

resource "aws_route_table" "public" {
  vpc_id = aws_vpc.this.id

  tags = {
    Name = "${var.name}-public-rt"
  }
}

resource "aws_route" "public_internet" {
  route_table_id         = aws_route_table.public.id
  destination_cidr_block = "0.0.0.0/0"
  gateway_id             = aws_internet_gateway.this.id
}

resource "aws_route_table_association" "public" {
  for_each = aws_subnet.public

  subnet_id      = each.value.id
  route_table_id = aws_route_table.public.id
}

# NAT for private subnets (so ECS tasks can pull from ECR without public IPs).
resource "aws_eip" "nat" {
  domain = "vpc"

  tags = {
    Name = "${var.name}-nat-eip"
  }
}

resource "aws_nat_gateway" "this" {
  allocation_id = aws_eip.nat.id
  subnet_id     = values(aws_subnet.public)[0].id

  tags = {
    Name = "${var.name}-nat"
  }

  depends_on = [aws_internet_gateway.this]
}

resource "aws_route_table" "private" {
  vpc_id = aws_vpc.this.id

  tags = {
    Name = "${var.name}-private-rt"
  }
}

resource "aws_route" "private_nat" {
  route_table_id         = aws_route_table.private.id
  destination_cidr_block = "0.0.0.0/0"
  nat_gateway_id         = aws_nat_gateway.this.id
}

resource "aws_route_table_association" "private" {
  for_each = aws_subnet.private

  subnet_id      = each.value.id
  route_table_id = aws_route_table.private.id
}

# Security group for the DB.
resource "aws_security_group" "db" {
  name        = "${var.name}-db-sg"
  description = "RDS MySQL access"
  vpc_id      = aws_vpc.this.id

  tags = {
    Name = "${var.name}-db-sg"
  }
}

# Security group for the migrations runner (ECS tasks).
resource "aws_security_group" "runner" {
  name        = "${var.name}-runner-sg"
  description = "ECS tasks running Atlas migrations"
  vpc_id      = aws_vpc.this.id

  egress {
    description = "Allow all egress"
    from_port   = 0
    to_port     = 0
    protocol    = "-1"
    cidr_blocks = ["0.0.0.0/0"]
  }

  tags = {
    Name = "${var.name}-runner-sg"
  }
}

# Allow runner -> DB.
resource "aws_security_group_rule" "db_ingress_from_runner" {
  type                     = "ingress"
  security_group_id        = aws_security_group.db.id
  from_port                = var.db_port
  to_port                  = var.db_port
  protocol                 = "tcp"
  source_security_group_id = aws_security_group.runner.id
  description              = "MySQL from migrations runner"
}

# Optional: allow direct access from CIDRs (only if DB is public).
resource "aws_security_group_rule" "db_ingress_from_cidrs" {
  count             = var.db_publicly_accessible && length(var.allowed_cidrs) > 0 ? 1 : 0
  type              = "ingress"
  security_group_id = aws_security_group.db.id
  from_port         = var.db_port
  to_port           = var.db_port
  protocol          = "tcp"
  cidr_blocks       = var.allowed_cidrs
  description       = "MySQL from allowed CIDRs"
}

resource "aws_db_subnet_group" "this" {
  name       = "${var.name}-db-subnets"
  subnet_ids = [for s in aws_subnet.private : s.id]

  tags = {
    Name = "${var.name}-db-subnets"
  }
}

resource "aws_kms_key" "rds" {
  description             = "KMS key for RDS encryption (${var.name})"
  deletion_window_in_days = 7
  enable_key_rotation     = true
}

resource "aws_kms_alias" "rds" {
  name          = "alias/${var.name}-rds"
  target_key_id = aws_kms_key.rds.key_id
}

resource "random_password" "db" {
  length  = 24
  special = true
}

resource "aws_secretsmanager_secret" "db" {
  name                    = "${var.name}/rds/master"
  recovery_window_in_days = 7
  kms_key_id              = aws_kms_key.rds.arn
}

resource "aws_secretsmanager_secret_version" "db" {
  secret_id = aws_secretsmanager_secret.db.id
  secret_string = jsonencode({
    username = var.db_username
    password = random_password.db.result
    dbname   = var.db_name
    port     = var.db_port
  })
}

resource "aws_db_instance" "mysql" {
  identifier = "${var.name}-mysql"

  engine         = "mysql"
  engine_version = "8.0"

  instance_class        = var.db_instance_class
  allocated_storage     = var.db_allocated_storage
  max_allocated_storage = var.db_max_allocated_storage
  storage_type          = "gp3"

  db_name  = var.db_name
  username = var.db_username
  password = random_password.db.result
  port     = var.db_port

  vpc_security_group_ids = [aws_security_group.db.id]
  db_subnet_group_name   = aws_db_subnet_group.this.name

  publicly_accessible     = var.db_publicly_accessible
  multi_az                = var.db_multi_az
  backup_retention_period = var.db_backup_retention_period

  storage_encrypted = true
  kms_key_id        = aws_kms_key.rds.arn

  deletion_protection = var.db_deletion_protection
  skip_final_snapshot = true

  enabled_cloudwatch_logs_exports = ["error", "general", "slowquery"]

  tags = {
    Name = "${var.name}-mysql"
  }
}

# ECR repository for the migrations image.
resource "aws_ecr_repository" "migrations" {
  name                 = "${var.name}-migrations"
  image_tag_mutability = "MUTABLE"

  image_scanning_configuration {
    scan_on_push = true
  }
}

resource "aws_ecr_lifecycle_policy" "migrations" {
  repository = aws_ecr_repository.migrations.name

  policy = jsonencode({
    rules = [
      {
        rulePriority = 1
        description  = "Keep last 30 images"
        selection = {
          tagStatus   = "any"
          countType   = "imageCountMoreThan"
          countNumber = 30
        }
        action = {
          type = "expire"
        }
      }
    ]
  })
}

# ECS cluster + task definition to run Atlas migrations as a one-off task.
resource "aws_ecs_cluster" "this" {
  name = "${var.name}-cluster"
}

resource "aws_cloudwatch_log_group" "migrations" {
  name              = "/ecs/${var.name}/migrations"
  retention_in_days = 30
}

data "aws_iam_policy_document" "ecs_task_assume" {
  statement {
    effect = "Allow"
    principals {
      type        = "Service"
      identifiers = ["ecs-tasks.amazonaws.com"]
    }
    actions = ["sts:AssumeRole"]
  }
}

resource "aws_iam_role" "ecs_task_execution" {
  name               = "${var.name}-ecs-task-exec"
  assume_role_policy = data.aws_iam_policy_document.ecs_task_assume.json
}

resource "aws_iam_role_policy_attachment" "ecs_task_execution" {
  role       = aws_iam_role.ecs_task_execution.name
  policy_arn = "arn:aws:iam::aws:policy/service-role/AmazonECSTaskExecutionRolePolicy"
}

resource "aws_iam_role" "ecs_task" {
  name               = "${var.name}-ecs-task"
  assume_role_policy = data.aws_iam_policy_document.ecs_task_assume.json
}

data "aws_iam_policy_document" "ecs_task" {
  statement {
    sid     = "ReadDbSecret"
    effect  = "Allow"
    actions = ["secretsmanager:GetSecretValue"]
    resources = [
      aws_secretsmanager_secret.db.arn
    ]
  }

  statement {
    sid     = "DecryptKms"
    effect  = "Allow"
    actions = ["kms:Decrypt"]
    resources = [
      aws_kms_key.rds.arn
    ]
  }
}

resource "aws_iam_policy" "ecs_task" {
  name   = "${var.name}-ecs-task"
  policy = data.aws_iam_policy_document.ecs_task.json
}

resource "aws_iam_role_policy_attachment" "ecs_task" {
  role       = aws_iam_role.ecs_task.name
  policy_arn = aws_iam_policy.ecs_task.arn
}

# NOTE: This task definition expects an image to exist in ECR.
# You can set var.migrations_image_tag to a pushed tag (e.g., git SHA).
variable "migrations_image_tag" {
  description = "ECR image tag for the migrations container."
  type        = string
  default     = "latest"
}

resource "aws_ecs_task_definition" "migrations" {
  family                   = "${var.name}-migrations"
  requires_compatibilities = ["FARGATE"]
  network_mode             = "awsvpc"
  cpu                      = "256"
  memory                   = "512"

  execution_role_arn = aws_iam_role.ecs_task_execution.arn
  task_role_arn      = aws_iam_role.ecs_task.arn

  container_definitions = jsonencode([
    {
      name      = "atlas"
      image     = "${aws_ecr_repository.migrations.repository_url}:${var.migrations_image_tag}"
      essential = true

      # The repo Dockerfile only copies /migrations. For a real run you likely also
      # want atlas.hcl + schema.hcl in the image, or mount them. This is a minimal
      # runner that demonstrates the pattern.
      command = [
        "migrate",
        "apply",
        "--dir",
        "file:///migrations",
        "--url",
        "${local.db_url_placeholder}"
      ]

      logConfiguration = {
        logDriver = "awslogs"
        options = {
          awslogs-group         = aws_cloudwatch_log_group.migrations.name
          awslogs-region        = var.aws_region
          awslogs-stream-prefix = "atlas"
        }
      }
    }
  ])

  lifecycle {
    ignore_changes = [container_definitions]
  }
}

# Placeholder to keep Terraform valid without embedding secrets in state.
# In practice, you should pass the DB URL at runtime (e.g., via ECS RunTask overrides)
# using the secret value from Secrets Manager.
locals {
  db_url_placeholder = "mysql://REPLACE_ME:REPLACE_ME@${aws_db_instance.mysql.address}:${var.db_port}/${var.db_name}"
}

output "vpc_id" {
  value       = aws_vpc.this.id
  description = "VPC ID"
}

output "private_subnet_ids" {
  value       = [for s in aws_subnet.private : s.id]
  description = "Private subnet IDs"
}

output "db_endpoint" {
  value       = aws_db_instance.mysql.address
  description = "RDS endpoint address"
}

output "db_port" {
  value       = var.db_port
  description = "RDS port"
}

output "db_name" {
  value       = var.db_name
  description = "Database name"
}

output "db_secret_arn" {
  value       = aws_secretsmanager_secret.db.arn
  description = "Secrets Manager secret ARN containing DB credentials"
}

output "ecr_repository_url" {
  value       = aws_ecr_repository.migrations.repository_url
  description = "ECR repository URL for the migrations image"
}

output "ecs_cluster_name" {
  value       = aws_ecs_cluster.this.name
  description = "ECS cluster name"
}

output "ecs_task_definition_arn" {
  value       = aws_ecs_task_definition.migrations.arn
  description = "ECS task definition ARN for migrations runner"
}

output "runner_security_group_id" {
  value       = aws_security_group.runner.id
  description = "Security group ID to attach to migrations runner tasks"
}
