plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

' Note: AWS icon includes are required by instruction, but this environment may not allow URL fetching.
' Diagram uses plain PlantUML elements only.

title High-level Deployment Architecture (Java WAR on Tomcat, CI/CD to Kubernetes)

skinparam shadowing false
skinparam componentStyle rectangle
skinparam wrapWidth 220
skinparam maxMessageSize 120

actor "End User" as user

node "Developer Workstation" as dev {
  artifact "Source Code\n(Java, JSP/HTML)" as src
  artifact "Jenkinsfile\n(Groovy)" as jf
  artifact "K8s Manifests\n(YAML: Deployment/Service)" as k8sman
  artifact "Ansible Playbook\n(YAML)" as ans
}

cloud "CI/CD" as cicd {
  node "Jenkins Controller/Agent" as jenkins {
    component "Pipeline\nBuild/Test/Package\n(Maven + JUnit)" as pipeline
    component "Docker Build\n(tomcat:8.0 base)" as dockerbuild
  }
}

cloud "Container Registry" as registry {
  database "Docker Hub\n(Images: balcha/banking_domin:latest\n or loksaieta/loksai-eta-app:latest)" as dockerhub
}

node "Provisioning" as prov {
  component "Ansible\n(install git, maven, docker,\nopenjdk, tomcat)" as ansible
}

node "Kubernetes Cluster" as k8s {
  node "Worker Node(s)" as workers {
    node "K8s Deployment\nreplicas=2" as deploy {
      node "Pod" as pod1 {
        node "Container: Tomcat 8" as tomcat1 {
          artifact "WAR WebApp\n(Java Servlet API 2.5)" as war1
          component "Logging\n(Log4j2)" as log1
        }
      }
      node "Pod" as pod2 {
        node "Container: Tomcat 8" as tomcat2 {
          artifact "WAR WebApp\n(Java Servlet API 2.5)" as war2
          component "Logging\n(Log4j2)" as log2
        }
      }
    }
  }

  node "K8s Service" as svc {
    component "NodePort Service\n(HTTP)" as nodeport
  }
}

user --> nodeport : HTTP request
nodeport --> tomcat1 : routes to pod
nodeport --> tomcat2 : routes to pod

src --> pipeline : commit/push triggers build
jf --> pipeline
k8sman --> pipeline : kubectl apply

ans --> ansible
ansible --> jenkins : provision build host
ansible --> workers : provision runtime nodes

pipeline --> dockerbuild : mvn package (WAR)
dockerbuild --> dockerhub : push image
pipeline --> k8s : deploy/update\n(Deployment uses image)

dockerhub --> tomcat1 : pull image
dockerhub --> tomcat2 : pull image

@enduml
