files.content:
terraform {
  required_version = ">= 1.6.0"

  required_providers {
    aws = {
      source  = "hashicorp/aws"
      version = "~> 5.0"
    }
    kubernetes = {
      source  = "hashicorp/kubernetes"
      version = "~> 2.25"
    }
    helm = {
      source  = "hashicorp/helm"
      version = "~> 2.13"
    }
    tls = {
      source  = "hashicorp/tls"
      version = "~> 4.0"
    }
  }
}

provider "aws" {
  region = var.aws_region

  default_tags {
    tags = {
      Project     = var.project_name
      Environment = var.environment
      ManagedBy   = "terraform"
    }
  }
}

# These providers are configured after the EKS cluster exists.
# Terraform will still be able to plan; apply will create EKS first.
provider "kubernetes" {
  host                   = module.eks.cluster_endpoint
  cluster_ca_certificate = base64decode(module.eks.cluster_certificate_authority_data)
  token                  = data.aws_eks_cluster_auth.this.token
}

provider "helm" {
  kubernetes {
    host                   = module.eks.cluster_endpoint
    cluster_ca_certificate = base64decode(module.eks.cluster_certificate_authority_data)
    token                  = data.aws_eks_cluster_auth.this.token
  }
}

variable "project_name" {
  description = "Project/name prefix used for resources."
  type        = string
  default     = "stackfusion"
}

variable "environment" {
  description = "Environment name (e.g., dev, staging, prod)."
  type        = string
  default     = "dev"
}

variable "aws_region" {
  description = "AWS region to deploy into."
  type        = string
  default     = "us-east-1"
}

variable "vpc_cidr" {
  description = "CIDR for the VPC."
  type        = string
  default     = "10.0.0.0/16"
}

variable "az_count" {
  description = "Number of AZs to use (2 recommended)."
  type        = number
  default     = 2
}

variable "eks_cluster_version" {
  description = "EKS Kubernetes version."
  type        = string
  default     = "1.29"
}

variable "node_instance_types" {
  description = "Instance types for the EKS managed node group."
  type        = list(string)
  default     = ["t3.medium"]
}

variable "node_desired_size" {
  description = "Desired node count."
  type        = number
  default     = 2
}

variable "node_min_size" {
  description = "Minimum node count."
  type        = number
  default     = 1
}

variable "node_max_size" {
  description = "Maximum node count."
  type        = number
  default     = 4
}

variable "app_namespace" {
  description = "Kubernetes namespace for the application."
  type        = string
  default     = "stackfusion"
}

variable "app_image" {
  description = "Container image URI (ECR recommended)."
  type        = string
  default     = "balcha/stackfusion:latest"
}

variable "app_replicas" {
  description = "Number of application replicas."
  type        = number
  default     = 1
}

variable "app_container_port" {
  description = "Container port exposed by the app."
  type        = number
  default     = 80
}

variable "enable_jenkins" {
  description = "Whether to provision a Jenkins controller EC2 instance."
  type        = bool
  default     = false
}

variable "jenkins_instance_type" {
  description = "EC2 instance type for Jenkins controller."
  type        = string
  default     = "t3.small"
}

variable "jenkins_allowed_cidrs" {
  description = "CIDRs allowed to access Jenkins UI (8080). Prefer VPN/corporate IPs."
  type        = list(string)
  default     = []
}

variable "route53_zone_id" {
  description = "(Optional) Route53 hosted zone id for app DNS record. If null, no record is created."
  type        = string
  default     = null
}

variable "app_domain_name" {
  description = "(Optional) FQDN for the app (e.g., app.example.com). Requires route53_zone_id."
  type        = string
  default     = null
}

data "aws_availability_zones" "available" {
  state = "available"
}

locals {
  name = "${var.project_name}-${var.environment}"

  azs = slice(data.aws_availability_zones.available.names, 0, var.az_count)

  # Simple deterministic subnetting: /16 -> /20s
  public_subnets  = [for i, az in local.azs : cidrsubnet(var.vpc_cidr, 4, i)]
  private_subnets = [for i, az in local.azs : cidrsubnet(var.vpc_cidr, 4, i + 8)]

  tags = {
    Project     = var.project_name
    Environment = var.environment
  }
}

resource "aws_ecr_repository" "app" {
  name                 = "${local.name}-app"
  image_tag_mutability = "MUTABLE"

  image_scanning_configuration {
    scan_on_push = true
  }

  encryption_configuration {
    encryption_type = "AES256"
  }

  tags = local.tags
}

resource "aws_ecr_lifecycle_policy" "app" {
  repository = aws_ecr_repository.app.name

  policy = jsonencode({
    rules = [
      {
        rulePriority = 1
        description  = "Keep last 30 images"
        selection = {
          tagStatus   = "any"
          countType   = "imageCountMoreThan"
          countNumber = 30
        }
        action = {
          type = "expire"
        }
      }
    ]
  })
}

resource "kubernetes_namespace" "kube_system" {
  metadata {
    name = "kube-system"
  }
}

# IAM role for AWS Load Balancer Controller (IRSA)
module "lb_controller_irsa" {
  source  = "terraform-aws-modules/iam/aws//modules/iam-role-for-service-accounts-eks"
  version = "~> 5.0"

  role_name                              = "${local.name}-aws-load-balancer-controller"
  attach_load_balancer_controller_policy = true

  oidc_providers = {
    main = {
      provider_arn               = module.eks.oidc_provider_arn
      namespace_service_accounts = ["kube-system:aws-load-balancer-controller"]
    }
  }

  tags = local.tags
}

resource "helm_release" "aws_load_balancer_controller" {
  name       = "aws-load-balancer-controller"
  repository = "https://aws.github.io/eks-charts"
  chart      = "aws-load-balancer-controller"
  namespace  = kubernetes_namespace.kube_system.metadata[0].name
  version    = "1.7.2"

  set {
    name  = "clusterName"
    value = module.eks.cluster_name
  }

  set {
    name  = "serviceAccount.create"
    value = "true"
  }

  set {
    name  = "serviceAccount.name"
    value = "aws-load-balancer-controller"
  }

  set {
    name  = "serviceAccount.annotations.eks\\.amazonaws\\.com/role-arn"
    value = module.lb_controller_irsa.iam_role_arn
  }

  set {
    name  = "region"
    value = var.aws_region
  }

  set {
    name  = "vpcId"
    value = module.vpc.vpc_id
  }

  depends_on = [module.eks]
}

resource "kubernetes_namespace" "app" {
  metadata {
    name = var.app_namespace
    labels = {
      "app.kubernetes.io/name" = var.project_name
    }
  }
}

resource "kubernetes_deployment" "app" {
  metadata {
    name      = "stackfusion"
    namespace = kubernetes_namespace.app.metadata[0].name
    labels = {
      app = "stackfusion"
    }
  }

  spec {
    replicas = var.app_replicas

    selector {
      match_labels = {
        app = "stackfusion"
      }
    }

    template {
      metadata {
        labels = {
          app = "stackfusion"
        }
      }

      spec {
        container {
          name  = "stackfusion"
          image = var.app_image

          port {
            container_port = var.app_container_port
          }

          resources {
            requests = {
              cpu    = "100m"
              memory = "128Mi"
            }
            limits = {
              cpu    = "500m"
              memory = "512Mi"
            }
          }

          liveness_probe {
            http_get {
              path = "/"
              port = var.app_container_port
            }
            initial_delay_seconds = 30
            period_seconds        = 10
          }

          readiness_probe {
            http_get {
              path = "/"
              port = var.app_container_port
            }
            initial_delay_seconds = 10
            period_seconds        = 10
          }
        }
      }
    }
  }

  depends_on = [helm_release.aws_load_balancer_controller]
}

resource "kubernetes_service" "app" {
  metadata {
    name      = "stackfusion"
    namespace = kubernetes_namespace.app.metadata[0].name
    labels = {
      app = "stackfusion"
    }
  }

  spec {
    selector = {
      app = "stackfusion"
    }

    port {
      port        = 80
      target_port = var.app_container_port
      protocol    = "TCP"
    }

    type = "ClusterIP"
  }
}

resource "kubernetes_ingress_v1" "app" {
  metadata {
    name      = "stackfusion"
    namespace = kubernetes_namespace.app.metadata[0].name

    annotations = {
      "kubernetes.io/ingress.class"            = "alb"
      "alb.ingress.kubernetes.io/scheme"       = "internet-facing"
      "alb.ingress.kubernetes.io/target-type"  = "ip"
      "alb.ingress.kubernetes.io/listen-ports" = "[{\"HTTP\":80}]"
      # For HTTPS, add certificate ARN and listen-ports 443.
    }
  }

  spec {
    rule {
      http {
        path {
          path      = "/"
          path_type = "Prefix"

          backend {
            service {
              name = kubernetes_service.app.metadata[0].name
              port {
                number = 80
              }
            }
          }
        }
      }
    }
  }

  depends_on = [helm_release.aws_load_balancer_controller]
}

data "aws_ami" "al2023" {
  most_recent = true
  owners      = ["amazon"]

  filter {
    name   = "name"
    values = ["al2023-ami-*-x86_64"]
  }
}

resource "aws_security_group" "jenkins" {
  count = var.enable_jenkins ? 1 : 0

  name        = "${local.name}-jenkins"
  description = "Jenkins controller security group"
  vpc_id      = module.vpc.vpc_id

  ingress {
    description = "Jenkins UI"
    from_port   = 8080
    to_port     = 8080
    protocol    = "tcp"
    cidr_blocks = length(var.jenkins_allowed_cidrs) > 0 ? var.jenkins_allowed_cidrs : ["127.0.0.1/32"]
  }

  ingress {
    description = "SSH (optional)"
    from_port   = 22
    to_port     = 22
    protocol    = "tcp"
    cidr_blocks = length(var.jenkins_allowed_cidrs) > 0 ? var.jenkins_allowed_cidrs : ["127.0.0.1/32"]
  }

  egress {
    from_port   = 0
    to_port     = 0
    protocol    = "-1"
    cidr_blocks = ["0.0.0.0/0"]
  }

  tags = local.tags
}

resource "aws_iam_role" "jenkins" {
  count = var.enable_jenkins ? 1 : 0

  name = "${local.name}-jenkins"

  assume_role_policy = jsonencode({
    Version = "2012-10-17"
    Statement = [
      {
        Effect = "Allow"
        Principal = {
          Service = "ec2.amazonaws.com"
        }
        Action = "sts:AssumeRole"
      }
    ]
  })

  tags = local.tags
}

resource "aws_iam_role_policy_attachment" "jenkins_ssm" {
  count      = var.enable_jenkins ? 1 : 0
  role       = aws_iam_role.jenkins[0].name
  policy_arn = "arn:aws:iam::aws:policy/AmazonSSMManagedInstanceCore"
}

resource "aws_iam_role_policy_attachment" "jenkins_ecr" {
  count      = var.enable_jenkins ? 1 : 0
  role       = aws_iam_role.jenkins[0].name
  policy_arn = "arn:aws:iam::aws:policy/AmazonEC2ContainerRegistryPowerUser"
}

resource "aws_iam_role_policy_attachment" "jenkins_eks" {
  count      = var.enable_jenkins ? 1 : 0
  role       = aws_iam_role.jenkins[0].name
  policy_arn = "arn:aws:iam::aws:policy/AmazonEKSClusterPolicy"
}

resource "aws_iam_instance_profile" "jenkins" {
  count = var.enable_jenkins ? 1 : 0
  name  = "${local.name}-jenkins"
  role  = aws_iam_role.jenkins[0].name
}

resource "aws_instance" "jenkins" {
  count = var.enable_jenkins ? 1 : 0

  ami                    = data.aws_ami.al2023.id
  instance_type          = var.jenkins_instance_type
  subnet_id              = module.vpc.private_subnets[0]
  vpc_security_group_ids = [aws_security_group.jenkins[0].id]
  iam_instance_profile   = aws_iam_instance_profile.jenkins[0].name

  user_data = <<-EOF
              #!/bin/bash
              set -euo pipefail
              dnf -y update
              dnf -y install java-17-amazon-corretto git docker
              systemctl enable --now docker
              # Jenkins install (simple):
              curl -fsSL https://pkg.jenkins.io/redhat-stable/jenkins.io-2023.key | tee /etc/pki/rpm-gpg/jenkins.io.key
              curl -fsSL https://pkg.jenkins.io/redhat-stable/jenkins.repo | tee /etc/yum.repos.d/jenkins.repo
              dnf -y install jenkins
              systemctl enable --now jenkins
              EOF

  tags = merge(local.tags, { Name = "${local.name}-jenkins" })

  depends_on = [module.vpc]
}

resource "aws_acm_certificate" "app" {
  count = var.app_domain_name != null && var.route53_zone_id != null ? 1 : 0

  domain_name       = var.app_domain_name
  validation_method = "DNS"

  lifecycle {
    create_before_destroy = true
  }

  tags = local.tags
}

resource "aws_route53_record" "app_cert_validation" {
  for_each = var.app_domain_name != null && var.route53_zone_id != null ? {
    for dvo in aws_acm_certificate.app[0].domain_validation_options : dvo.domain_name => {
      name   = dvo.resource_record_name
      record = dvo.resource_record_value
      type   = dvo.resource_record_type
    }
  } : {}

  zone_id = var.route53_zone_id
  name    = each.value.name
  type    = each.value.type
  ttl     = 60
  records = [each.value.record]
}

resource "aws_acm_certificate_validation" "app" {
  count = var.app_domain_name != null && var.route53_zone_id != null ? 1 : 0

  certificate_arn         = aws_acm_certificate.app[0].arn
  validation_record_fqdns = [for r in aws_route53_record.app_cert_validation : r.fqdn]
}

# NOTE: Creating a Route53 ALIAS to the ALB requires the ALB DNS name/zone id.
# The ALB is created dynamically by the AWS Load Balancer Controller.
# For that reason, DNS record creation is intentionally omitted here.
# If you need it, you can:
#  - manage the ALB as an aws_lb resource (instead of controller), or
#  - read the ALB via data sources after it exists and then create the record.

output "vpc_id" {
  value = module.vpc.vpc_id
}

output "eks_cluster_name" {
  value = module.eks.cluster_name
}

output "eks_cluster_endpoint" {
  value = module.eks.cluster_endpoint
}

output "ecr_repository_url" {
  value = aws_ecr_repository.app.repository_url
}

output "app_namespace" {
  value = var.app_namespace
}

output "jenkins_instance_private_ip" {
  value       = var.enable_jenkins ? aws_instance.jenkins[0].private_ip : null
  description = "Private IP of Jenkins controller (if enabled)."
}
