files.content:
terraform {
  required_version = ">= 1.5.0"

  required_providers {
    aws = {
      source  = "hashicorp/aws"
      version = "~> 5.0"
    }
  }
}

provider "aws" {
  region = var.aws_region

  default_tags {
    tags = var.tags
  }
}

variable "aws_region" {
  description = "AWS region to deploy into."
  type        = string
  default     = "us-east-1"
}

variable "name" {
  description = "Name prefix for resources."
  type        = string
  default     = "s3-versioning-demo"
}

variable "bucket_name" {
  description = "S3 bucket name to store versioned objects. Must be globally unique."
  type        = string
  default     = "versionedexample"
}

variable "tags" {
  description = "Tags applied to all supported resources."
  type        = map(string)
  default = {
    Project = "s3-versioning-demo"
    Managed = "terraform"
  }
}

resource "aws_s3_bucket" "versioned" {
  bucket = var.bucket_name

  # Prevent accidental deletion of the bucket (and all versions) in production.
  # Set to false if you want to allow terraform destroy.
  force_destroy = false
}

resource "aws_s3_bucket_versioning" "this" {
  bucket = aws_s3_bucket.versioned.id

  versioning_configuration {
    status = "Enabled"
  }
}

resource "aws_s3_bucket_public_access_block" "this" {
  bucket = aws_s3_bucket.versioned.id

  block_public_acls       = true
  block_public_policy     = true
  ignore_public_acls      = true
  restrict_public_buckets = true
}

resource "aws_s3_bucket_server_side_encryption_configuration" "this" {
  bucket = aws_s3_bucket.versioned.id

  rule {
    apply_server_side_encryption_by_default {
      sse_algorithm = "AES256"
    }
  }
}

output "bucket_name" {
  description = "Name of the S3 bucket used by the application."
  value       = aws_s3_bucket.versioned.bucket
}

output "bucket_arn" {
  description = "ARN of the S3 bucket used by the application."
  value       = aws_s3_bucket.versioned.arn
}
