plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

title High-level Deployment Architecture - Renovate Bot Automation

skinparam shadowing false
skinparam componentStyle rectangle

node "Execution Environment\n(CI Runner / Scheduler)" as runner {
  artifact "Container Image\nghcr.io/renovatebot/renovate:36.72.0" as image
  component "Renovate Bot\n(Automation Job)" as renovate
  file "renovate.json\n(preset: config:recommended, :dependencyDashboard)" as config
}

cloud "Git Hosting" as git {
  database "Source Code Repository\n(Git)" as repo
  component "Dependency Dashboard\n(Issues/PRs)" as dashboard
}

renovate --> repo : clone/fetch
config ..> renovate : configures
renovate --> dashboard : create/update PRs & issues
renovate ..> image : runs in

@enduml
