plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

title React SPA on Amazon EKS with GitHub Actions CI/CD

skinparam shadowing false
skinparam componentStyle rectangle
skinparam wrapWidth 220
skinparam maxMessageSize 120

actor "End User" as user
cloud "Internet" as internet

node "GitHub" as github {
  artifact "Source Repo\n(React 18 / CRA)" as repo
  node "GitHub Actions\n(Self-hosted runner)" as gha {
    component "Build & Test\nreact-scripts" as build
    component "SonarQube Scan\nsonarqube-scan-action" as sonarjob
    component "Trivy Image Scan" as trivy
    component "Deploy\nkubectl apply" as deploy
    component "Slack Notify\nact10ns/slack" as slackjob
  }
}

node "SonarQube Server" as sonarqube
node "Docker Hub\nContainer Registry" as dockerhub

node "AWS Cloud" as aws {
  node "Amazon EKS Cluster" as eks {
    node "Kubernetes Namespace" as ns {
      node "Deployment\nReact SPA container" as deploymnt {
        component "Pod(s)\nNginx/Node static server\nserving build/" as pods
      }
      node "Service\nType: LoadBalancer" as svc
    }
  }
}

user --> internet
internet --> svc : HTTPS
svc --> pods : HTTP

repo --> build : checkout
build --> sonarjob : analyze
sonarjob --> sonarqube : publish metrics
build --> dockerhub : docker build & push\n(image)
dockerhub --> trivy : scan image
trivy --> build : report
build --> deploy : trigger on main
deploy --> eks : kubectl apply\n(Deployment/Service)
deploy --> slackjob : notify
slackjob --> "Slack" : webhook

@enduml
