plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

title Local Development AWS Emulation (Docker Compose)

skinparam shadowing false
skinparam componentStyle rectangle

node "Developer Machine\n(macOS/Linux/Windows)" as dev {
  artifact "Tooling\n- Docker Engine + docker-compose\n- Terraform CLI\n- AWS CLI\n- Bash scripts (install.sh)" as tools

  node "Docker Engine" as docker {
    frame "Docker Compose Project" as compose {
      node "Bridge Network: local" as net {
        node "Container: LocalStack\nImage: localstack/localstack:latest\nPorts: 4566 (edge/health)" as localstack {
          database "Emulated AWS Services\n(e.g., DynamoDB)" as emu
        }

        node "Container: DynamoDB Admin\nImage: aaronshaf/dynamodb-admin\nPort: 8001 (Web UI)" as ddbadmin
      }

      folder "Bind Mount: ./volume" as hostvol
      folder "Container Volume: /var/lib/localstack\n(state/cache, certs)" as lsvol
    }
  }
}

hostvol -- lsvol : mounted into
localstack -- lsvol : reads/writes

ddbadmin --> localstack : DYNAMO_ENDPOINT\n(HTTP to 4566)

tools --> localstack : AWS CLI / Terraform\n(API calls via localhost:4566)

dev --> localstack : http://localhost:4566\n(edge/health)

dev --> ddbadmin : http://localhost:8001\n(DynamoDB Admin UI)

@enduml
