terraform {
  required_version = ">= 1.5.0"

  required_providers {
    aws = {
      source  = "hashicorp/aws"
      version = "~> 5.0"
    }
  }
}

provider "aws" {
  region                      = var.aws_region
  access_key                  = var.aws_access_key_id
  secret_key                  = var.aws_secret_access_key
  skip_credentials_validation = true
  skip_metadata_api_check     = true
  skip_requesting_account_id  = true

  # LocalStack endpoints
  endpoints {
    dynamodb = var.localstack_endpoint
    iam      = var.localstack_endpoint
    sts      = var.localstack_endpoint
  }

  default_tags {
    tags = var.tags
  }
}

variable "aws_region" {
  description = "AWS region (LocalStack uses this for request signing/partitioning)."
  type        = string
  default     = "eu-central-1"
}

variable "localstack_endpoint" {
  description = "LocalStack edge endpoint (e.g., http://localhost:4566)."
  type        = string
  default     = "http://localhost:4566"
}

variable "aws_access_key_id" {
  description = "Dummy access key for LocalStack."
  type        = string
  default     = "devopshobbies"
}

variable "aws_secret_access_key" {
  description = "Dummy secret key for LocalStack."
  type        = string
  default     = "devopshobbies"
  sensitive   = true
}

variable "dynamodb_table_name" {
  description = "Name of the DynamoDB table to create in LocalStack."
  type        = string
  default     = "example-table"
}

variable "tags" {
  description = "Default tags applied to resources."
  type        = map(string)
  default = {
    Project = "localstack"
    Managed = "terraform"
  }
}

resource "aws_dynamodb_table" "this" {
  name         = var.dynamodb_table_name
  billing_mode = "PAY_PER_REQUEST"
  hash_key     = "pk"
  range_key    = "sk"

  attribute {
    name = "pk"
    type = "S"
  }

  attribute {
    name = "sk"
    type = "S"
  }

  point_in_time_recovery {
    enabled = true
  }
}

output "dynamodb_table_name" {
  value       = aws_dynamodb_table.this.name
  description = "Created DynamoDB table name."
}

output "localstack_endpoint" {
  value       = var.localstack_endpoint
  description = "LocalStack edge endpoint used by the provider."
}
