plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

skinparam shadowing false
skinparam componentStyle rectangle

title Local AWS Emulation Stack (Docker Compose)

node "Developer Machine / Host OS" as host {
  node "Docker Engine" as docker {
    frame "Docker network: local (bridge)" as net {
      node "Container: LocalStack\nImage: localstack/localstack:latest" as localstack {
        artifact "Local AWS APIs (emulated)" as awsapis
        database "DynamoDB (emulated)" as dynamodb
        awsapis -down- dynamodb
      }

      node "Container: DynamoDB Admin\nImage: aaronshaf/dynamodb-admin" as admin {
        component "Web UI\n(dynamodb-admin)" as webui
      }

      admin --> localstack : HTTP (DYNAMO_ENDPOINT)
    }

    storage "Bind mount volume\n./volume -> /var/lib/localstack" as volume
    volume -- localstack : persisted state/cache
  }

  artifact "Provisioning/CLI Tools\nBash install.sh installs:\n- docker, docker-compose\n- awscli\n- terraform" as tools
}

@enduml
