plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

title Node.js HTTP Service on Kubernetes (CI builds/pushes image)

actor "User" as user

node "Kubernetes Cluster\n(e.g., Minikube in CI)" as k8s {
  node "Namespace: node" as ns {
    node "Service: node-service\nType: NodePort\nPort 8081 -> TargetPort 3000" as svc

    node "Deployment: node-deployment\nReplicas: 3" as dep {
      artifact "Container Image\nhendawyy/node-img:v1" as img
      node "Pod (x3)\nNode.js HTTP server\n(index.js, listens :3000)" as pod
    }
  }
}

cloud "Container Registry\n(Docker Hub)" as registry

node "CI Pipeline\n(GitHub Actions)" as ci {
  component "Build & Test\n(Node.js)" as build
  component "Docker Build" as dockerbuild
  component "Push Image" as push
  component "Deploy to K8s\n(apply manifests)" as deploy
  component "Terraform Stage\n(init/validate/plan/apply)\n(no .tf in repo)" as tf
}

user --> svc : HTTP request\n(NodePort :8081)
svc --> pod : routes traffic\n(to :3000)

build --> dockerbuild
dockerbuild --> push
push --> registry : docker push
registry --> img : image pull

deploy --> k8s : kubectl apply\n(namespace/deployment/service)
ci --> tf : runs terraform\n(init/validate)

@enduml
