plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

title React SPA on Kubernetes with CI/CD and External API

skinparam shadowing false
skinparam componentStyle rectangle
skinparam wrapWidth 220
skinparam maxMessageSize 120

actor "End User" as user

cloud "Internet" as internet

node "Kubernetes Cluster" as k8s {
  node "Cloud Load Balancer\n(via Service: LoadBalancer)" as lb

  node "K8s Namespace" as ns {
    artifact "Service\nType: LoadBalancer\nPort: 3000" as svc
    node "Deployment (apps/v1)\nReplicas: 1" as deploy {
      node "Pod" as pod {
        artifact "Container: React SPA\nNginx/Node static server\n(react-scripts build output)\nPort: 3000" as app
      }
    }
  }
}

cloud "Third-Party API" as ext {
  artifact "Official Joke API\nhttps://official-joke-api.appspot.com/random_joke" as jokeapi
}

node "CI/CD" as cicd {
  node "Jenkins" as jenkins {
    artifact "Pipeline (Jenkinsfile)\n- Build & Test (Node)\n- SonarQube\n- OWASP Dependency-Check\n- Trivy + Docker Scout\n- Build Docker Image\n- Push to DockerHub" as jpipe
  }
  node "GitHub Actions" as gha {
    artifact "node.js.yml\n- install/test/build" as ghci
    artifact "npm-publish.yml\n- publish package" as ghnpm
  }
  artifact "Source Repo\n(GitHub)" as repo
}

cloud "Container Registry" as reg {
  artifact "DockerHub\nImage: yash5090/react-jg-app:latest" as dockerhub
}

user --> internet
internet --> lb : HTTPS
lb --> svc
svc --> app : HTTP :3000

app --> jokeapi : fetch() JSON

repo --> jenkins : webhook / poll
repo --> gha : push / PR

jenkins --> dockerhub : push image
k8s --> dockerhub : pull image

ghci --> repo : status checks

ghnpm --> repo : release tags

@enduml
