plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

skinparam shadowing false
skinparam componentStyle rectangle
skinparam wrapWidth 220
skinparam maxMessageSize 120

title High-level Deployment Architecture (Flask on Kubernetes with AWS Load Balancer + CI/CD)

actor "End User" as user

cloud "AWS" as aws {
  node "AWS Load Balancer\n(Provisioned by K8s Service\nType=LoadBalancer / ALB annotations)" as alb

  node "Kubernetes Cluster\n(Amazon EKS or self-managed on AWS)" as k8s {
    node "Kubernetes Service\nType=LoadBalancer" as svc

    node "Kubernetes Deployment\ncalculator-deployment\nreplicas=2" as deploy {
      node "Pod (replica)" as pod1 {
        artifact "Container Image\nPython + Flask app\n(app.py + templates)" as img
        component "Flask Web App\nPython / Flask\nServes HTML/CSS" as flask
      }
      node "Pod (replica)" as pod2 {
        artifact "Container Image\nPython + Flask app\n(app.py + templates)" as img2
        component "Flask Web App\nPython / Flask\nServes HTML/CSS" as flask2
      }
    }
  }
}

frame "CI/CD" as cicd {
  node "GitHub" as github {
    artifact "Source Repository" as repo
    node "GitHub Actions\n(docker-publish.yml)" as gha
  }

  node "Jenkins" as jenkins {
    component "Pipeline (Jenkinsfile)" as pipeline
    component "SonarQube\nCode Quality" as sonarqube
    component "OWASP Dependency-Check\nSCA" as owasp
    component "Trivy\nContainer Scan" as trivy
    component "Docker Scout\nImage Insights" as scout
  }

  database "Container Registry\n(DockerHub / GHCR)" as registry
}

' Runtime traffic
user --> alb : HTTPS
alb --> svc : forwards
svc --> flask : HTTP
svc --> flask2 : HTTP

' Build & deploy flow
repo --> gha : push/PR triggers
repo --> pipeline : webhook / poll

gha --> registry : build & push image\n(+ sign)
pipeline --> sonarqube : analyze
pipeline --> owasp : dependency scan
pipeline --> trivy : image scan
pipeline --> scout : image analysis
pipeline --> registry : docker build & push
pipeline --> k8s : kubectl apply\n(deployment.yaml/service.yaml\n+ manifest/*)

registry --> img : pull image
registry --> img2 : pull image

@enduml
