plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

title High-level Deployment Architecture (Node.js on EKS)

skinparam shadowing false
skinparam componentStyle rectangle

actor "End User" as user

cloud "Internet" as internet

node "GitHub" as github {
  node "GitHub Actions\n(CI/CD)" as gha {
    artifact "Workflow: ci.yml\n- Super-Linter\n- Terraform CLI 1.1.9\n- Docker build/push\n- kubectl apply" as workflow
  }
}

node "AWS Account 355001659877\nRegion: us-east-1" as aws {

  node "Amazon ECR\nRepo: my-ecr-repo" as ecr {
    artifact "Container Image\n(Node.js HTTP server)" as image
  }

  node "Amazon EKS\nCluster: nawy-eks-cluster" as eks {
    node "Kubernetes Namespace: nodeaws" as ns {
      node "Service: LoadBalancer\nPort 80 -> 3000" as svc
      node "Deployment: 3 replicas" as deploy {
        node "Pod (x3)\nContainer: Node.js\nApp listens :3000" as pod
      }
    }
    node "Cloud Load Balancer\n(provisioned by Service)" as clb
  }
}

user --> internet
internet --> clb : HTTP :80
clb --> svc : forwards
svc --> pod : routes to :3000

gha --> ecr : docker push
workflow ..> image : builds

gha --> eks : kubectl apply\n(Deployment/Service/Namespace)
workflow ..> eks : update-kubeconfig

workflow ..> aws : terraform init/plan/apply\n(IaC referenced; resources not in repo)

@enduml
