plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

title Local AWS Serverless Stack (LocalStack) - High-Level Deployment

skinparam shadowing false
skinparam componentStyle rectangle
skinparam wrapWidth 220

actor "Developer / Tester" as dev

node "Local Machine" as local {
  node "Docker Engine" as docker {
    node "LocalStack Container\n(localstack/localstack-pro)" as ls {
      component "API Gateway (REST API)\n(AWS emulated)" as apigw
      component "Lambda Function\n(Emulated AWS Lambda runtime)\nArtifact: product-lambda.jar or lambda.zip" as lambda
    }
  }

  component "Terraform Wrapper\n(tflocal)" as tflocal
  component "Invoke Scripts\n(invoke.sh / invoke-aws.sh)\nTools: curl" as scripts
}

dev --> scripts : Run test calls
scripts --> apigw : HTTP request\nexecute-api.*.localhost.localstack.cloud

tflocal --> ls : Provision emulated resources\n(API Gateway, Lambda, IAM, etc.)

apigw --> lambda : Invoke

@enduml
