terraform {
  required_version = ">= 1.5.0"

  required_providers {
    aws = {
      source  = "hashicorp/aws"
      version = "~> 5.0"
    }
    archive = {
      source  = "hashicorp/archive"
      version = "~> 2.4"
    }
  }
}

provider "aws" {
  region = var.aws_region

  default_tags {
    tags = var.tags
  }
}

variable "aws_region" {
  description = "AWS region to deploy into."
  type        = string
  default     = "eu-west-1"
}

variable "name" {
  description = "Name prefix for resources."
  type        = string
  default     = "hello-world"
}

variable "tags" {
  description = "Tags applied to all supported resources."
  type        = map(string)
  default = {
    Project = "test_sagemaker_deploy"
    Managed = "terraform"
  }
}

variable "ecr_repository_name" {
  description = "ECR repository name for the inference container image."
  type        = string
  default     = "test_sage_maker"
}

variable "image_tag" {
  description = "ECR image tag to deploy to SageMaker. You must build and push this tag to ECR separately."
  type        = string
  default     = "latest"
}

variable "sagemaker_endpoint_name" {
  description = "SageMaker endpoint name. Must match what clients/Lambda invoke."
  type        = string
  default     = "hello-world-endpoint"
}

variable "instance_type" {
  description = "SageMaker instance type for the endpoint."
  type        = string
  default     = "ml.t2.medium"
}

variable "initial_instance_count" {
  description = "Initial number of instances for the endpoint."
  type        = number
  default     = 1
}

variable "lambda_schedule_expression" {
  description = "EventBridge schedule expression. Set to null to disable scheduling. Example: rate(5 minutes) or cron(0/5 * * * ? *)."
  type        = string
  default     = "rate(5 minutes)"
}

variable "lambda_timeout_seconds" {
  description = "Lambda timeout in seconds."
  type        = number
  default     = 30
}

variable "lambda_memory_size" {
  description = "Lambda memory size in MB."
  type        = number
  default     = 256
}

resource "aws_ecr_repository" "inference" {
  name                 = var.ecr_repository_name
  image_tag_mutability = "MUTABLE"

  image_scanning_configuration {
    scan_on_push = true
  }
}

resource "aws_ecr_lifecycle_policy" "inference" {
  repository = aws_ecr_repository.inference.name

  policy = jsonencode({
    rules = [
      {
        rulePriority = 1
        description  = "Keep last 20 images"
        selection = {
          tagStatus   = "any"
          countType   = "imageCountMoreThan"
          countNumber = 20
        }
        action = {
          type = "expire"
        }
      }
    ]
  })
}

data "aws_iam_policy_document" "sagemaker_assume" {
  statement {
    effect = "Allow"
    actions = [
      "sts:AssumeRole",
    ]

    principals {
      type        = "Service"
      identifiers = ["sagemaker.amazonaws.com"]
    }
  }
}

resource "aws_iam_role" "sagemaker_execution" {
  name               = "${var.name}-sagemaker-exec"
  assume_role_policy = data.aws_iam_policy_document.sagemaker_assume.json
}

# Minimal permissions for SageMaker to pull from ECR and write logs.
# Note: SageMaker also uses CloudWatch Logs via the service; this policy is commonly sufficient for custom containers.
resource "aws_iam_role_policy_attachment" "sagemaker_ecr_readonly" {
  role       = aws_iam_role.sagemaker_execution.name
  policy_arn = "arn:aws:iam::aws:policy/AmazonEC2ContainerRegistryReadOnly"
}

resource "aws_iam_role_policy_attachment" "sagemaker_cw_logs" {
  role       = aws_iam_role.sagemaker_execution.name
  policy_arn = "arn:aws:iam::aws:policy/CloudWatchLogsFullAccess"
}

data "aws_iam_policy_document" "lambda_assume" {
  statement {
    effect = "Allow"
    actions = [
      "sts:AssumeRole",
    ]

    principals {
      type        = "Service"
      identifiers = ["lambda.amazonaws.com"]
    }
  }
}

resource "aws_iam_role" "lambda_execution" {
  name               = "${var.name}-lambda-exec"
  assume_role_policy = data.aws_iam_policy_document.lambda_assume.json
}

resource "aws_iam_role_policy_attachment" "lambda_basic" {
  role       = aws_iam_role.lambda_execution.name
  policy_arn = "arn:aws:iam::aws:policy/service-role/AWSLambdaBasicExecutionRole"
}

data "aws_iam_policy_document" "lambda_invoke_sagemaker" {
  statement {
    sid    = "InvokeSageMakerEndpoint"
    effect = "Allow"
    actions = [
      "sagemaker:InvokeEndpoint",
      "sagemaker:InvokeEndpointAsync",
    ]
    resources = [
      aws_sagemaker_endpoint.endpoint.arn,
    ]
  }
}

resource "aws_iam_policy" "lambda_invoke_sagemaker" {
  name   = "${var.name}-lambda-invoke-sagemaker"
  policy = data.aws_iam_policy_document.lambda_invoke_sagemaker.json
}

resource "aws_iam_role_policy_attachment" "lambda_invoke_sagemaker" {
  role       = aws_iam_role.lambda_execution.name
  policy_arn = aws_iam_policy.lambda_invoke_sagemaker.arn
}

data "aws_caller_identity" "current" {}

data "aws_region" "current" {}

locals {
  image_uri = "${aws_ecr_repository.inference.repository_url}:${var.image_tag}"
}

resource "aws_sagemaker_model" "model" {
  name               = "${var.name}-model"
  execution_role_arn = aws_iam_role.sagemaker_execution.arn

  primary_container {
    image = local.image_uri

    environment = {
      # Common for SageMaker inference containers; your app listens on 8080.
      SAGEMAKER_PROGRAM          = "app.py"
      SAGEMAKER_SUBMIT_DIRECTORY = "/usr/src/app"
    }
  }
}

resource "aws_sagemaker_endpoint_configuration" "cfg" {
  name = "${var.name}-endpoint-cfg"

  production_variants {
    variant_name           = "AllTraffic"
    model_name             = aws_sagemaker_model.model.name
    initial_instance_count = var.initial_instance_count
    instance_type          = var.instance_type
  }
}

resource "aws_sagemaker_endpoint" "endpoint" {
  name                 = var.sagemaker_endpoint_name
  endpoint_config_name = aws_sagemaker_endpoint_configuration.cfg.name
}

data "archive_file" "lambda_zip" {
  type        = "zip"
  source_file = "${path.module}/../lambda/src/lambda_function.py"
  output_path = "${path.module}/lambda_function.zip"
}

resource "aws_lambda_function" "invoker" {
  function_name = "${var.name}-sagemaker-invoker"
  role          = aws_iam_role.lambda_execution.arn
  handler       = "lambda_function.lambda_handler"
  runtime       = "python3.11"

  filename         = data.archive_file.lambda_zip.output_path
  source_code_hash = data.archive_file.lambda_zip.output_base64sha256

  timeout     = var.lambda_timeout_seconds
  memory_size = var.lambda_memory_size

  environment {
    variables = {
      SAGEMAKER_ENDPOINT_NAME = var.sagemaker_endpoint_name
      AWS_REGION              = var.aws_region
    }
  }

  depends_on = [
    aws_iam_role_policy_attachment.lambda_basic,
    aws_iam_role_policy_attachment.lambda_invoke_sagemaker,
  ]
}

resource "aws_cloudwatch_log_group" "lambda" {
  name              = "/aws/lambda/${aws_lambda_function.invoker.function_name}"
  retention_in_days = 14
}

resource "aws_cloudwatch_event_rule" "schedule" {
  count               = var.lambda_schedule_expression == null ? 0 : 1
  name                = "${var.name}-invoke-sagemaker-schedule"
  schedule_expression = var.lambda_schedule_expression
}

resource "aws_cloudwatch_event_target" "lambda" {
  count = var.lambda_schedule_expression == null ? 0 : 1

  rule      = aws_cloudwatch_event_rule.schedule[0].name
  target_id = "lambda"
  arn       = aws_lambda_function.invoker.arn
}

resource "aws_lambda_permission" "allow_eventbridge" {
  count = var.lambda_schedule_expression == null ? 0 : 1

  statement_id  = "AllowExecutionFromEventBridge"
  action        = "lambda:InvokeFunction"
  function_name = aws_lambda_function.invoker.function_name
  principal     = "events.amazonaws.com"
  source_arn    = aws_cloudwatch_event_rule.schedule[0].arn
}

output "ecr_repository_url" {
  description = "ECR repository URL (without tag)."
  value       = aws_ecr_repository.inference.repository_url
}

output "sagemaker_image_uri" {
  description = "Full image URI (including tag) used by SageMaker."
  value       = local.image_uri
}

output "sagemaker_endpoint_name" {
  description = "SageMaker endpoint name."
  value       = aws_sagemaker_endpoint.endpoint.name
}

output "lambda_function_name" {
  description = "Lambda function name that invokes the SageMaker endpoint."
  value       = aws_lambda_function.invoker.function_name
}
