files.content:
terraform {
  required_version = ">= 1.6.0"

  required_providers {
    aws = {
      source  = "hashicorp/aws"
      version = "~> 5.0"
    }
  }
}

provider "aws" {
  region = var.aws_region

  default_tags {
    tags = {
      Project     = var.project_name
      Environment = var.environment
      ManagedBy   = "terraform"
    }
  }
}

variable "aws_region" {
  description = "AWS region to deploy into."
  type        = string
  default     = "us-east-1"
}

variable "project_name" {
  description = "Project/name prefix used for resources."
  type        = string
  default     = "notes-webapp"
}

variable "environment" {
  description = "Environment name (e.g., dev, staging, prod)."
  type        = string
  default     = "dev"
}

variable "vpc_cidr" {
  description = "CIDR block for the VPC."
  type        = string
  default     = "10.0.0.0/16"
}

variable "az_count" {
  description = "Number of AZs to use (2 or 3)."
  type        = number
  default     = 2
  validation {
    condition     = contains([2, 3], var.az_count)
    error_message = "az_count must be 2 or 3."
  }
}

variable "single_nat_gateway" {
  description = "If true, create a single NAT gateway (lower cost). If false, one per AZ (higher availability)."
  type        = bool
  default     = true
}

variable "cluster_version" {
  description = "EKS Kubernetes version."
  type        = string
  default     = "1.29"
}

variable "node_instance_types" {
  description = "Instance types for the EKS managed node group."
  type        = list(string)
  default     = ["t3.medium"]
}

variable "node_desired_size" {
  description = "Desired number of worker nodes."
  type        = number
  default     = 2
}

variable "node_min_size" {
  description = "Minimum number of worker nodes."
  type        = number
  default     = 1
}

variable "node_max_size" {
  description = "Maximum number of worker nodes."
  type        = number
  default     = 4
}

variable "ecr_repository_name" {
  description = "ECR repository name for the application image."
  type        = string
  default     = "notes-webapp"
}

variable "enable_ecr_scan_on_push" {
  description = "Enable ECR image scanning on push."
  type        = bool
  default     = true
}

variable "ecr_image_tag_mutability" {
  description = "ECR tag mutability. IMMUTABLE is recommended."
  type        = string
  default     = "IMMUTABLE"
  validation {
    condition     = contains(["MUTABLE", "IMMUTABLE"], var.ecr_image_tag_mutability)
    error_message = "ecr_image_tag_mutability must be MUTABLE or IMMUTABLE."
  }
}

variable "app_image" {
  description = "Container image to deploy (e.g., <account>.dkr.ecr.<region>.amazonaws.com/notes-webapp:<tag>)."
  type        = string
  default     = "yash5090/notes-webapp:latest"
}

variable "app_replicas" {
  description = "Number of application replicas."
  type        = number
  default     = 2
}

variable "app_container_port" {
  description = "Container port exposed by the app."
  type        = number
  default     = 5000
}

variable "app_service_port" {
  description = "Service port exposed by the LoadBalancer service."
  type        = number
  default     = 5000
}

variable "create_route53" {
  description = "If true, create Route53 record for the ALB. Requires hosted_zone_id and domain_name."
  type        = bool
  default     = false
}

variable "hosted_zone_id" {
  description = "Route53 hosted zone ID (public). Required if create_route53=true."
  type        = string
  default     = ""
}

variable "domain_name" {
  description = "DNS name to create (e.g., notes.example.com). Required if create_route53=true."
  type        = string
  default     = ""
}

data "aws_availability_zones" "available" {
  state = "available"
}

locals {
  name = "${var.project_name}-${var.environment}"
  azs  = slice(data.aws_availability_zones.available.names, 0, var.az_count)

  # /20s give 16 subnets within a /16; we take first N for public and next N for private.
  public_subnets  = [for i in range(var.az_count) : cidrsubnet(var.vpc_cidr, 4, i)]
  private_subnets = [for i in range(var.az_count) : cidrsubnet(var.vpc_cidr, 4, i + var.az_count)]
}

module "vpc" {
  source  = "terraform-aws-modules/vpc/aws"
  version = "~> 5.0"

  name = local.name
  cidr = var.vpc_cidr

  azs             = local.azs
  public_subnets  = local.public_subnets
  private_subnets = local.private_subnets

  enable_nat_gateway   = true
  single_nat_gateway   = var.single_nat_gateway
  enable_dns_hostnames = true
  enable_dns_support   = true

  public_subnet_tags = {
    "kubernetes.io/role/elb" = "1"
  }

  private_subnet_tags = {
    "kubernetes.io/role/internal-elb" = "1"
  }
}

module "eks" {
  source  = "terraform-aws-modules/eks/aws"
  version = "~> 20.0"

  cluster_name    = local.name
  cluster_version = var.cluster_version

  vpc_id     = module.vpc.vpc_id
  subnet_ids = module.vpc.private_subnets

  cluster_endpoint_public_access = true

  enable_cluster_creator_admin_permissions = true

  cluster_addons = {
    coredns    = {}
    kube-proxy = {}
    vpc-cni    = {}
  }

  eks_managed_node_groups = {
    default = {
      name            = "default"
      instance_types  = var.node_instance_types
      min_size        = var.node_min_size
      max_size        = var.node_max_size
      desired_size    = var.node_desired_size
      capacity_type   = "ON_DEMAND"
      subnet_ids      = module.vpc.private_subnets

      # Allow pulling from ECR and basic node operations are handled by the module.
    }
  }
}

resource "aws_ecr_repository" "app" {
  name                 = var.ecr_repository_name
  image_tag_mutability = var.ecr_image_tag_mutability

  image_scanning_configuration {
    scan_on_push = var.enable_ecr_scan_on_push
  }

  encryption_configuration {
    encryption_type = "AES256"
  }
}

resource "aws_ecr_lifecycle_policy" "app" {
  repository = aws_ecr_repository.app.name

  policy = jsonencode({
    rules = [
      {
        rulePriority = 1
        description  = "Expire untagged images older than 14 days"
        selection = {
          tagStatus   = "untagged"
          countType   = "sinceImagePushed"
          countUnit   = "days"
          countNumber = 14
        }
        action = {
          type = "expire"
        }
      },
      {
        rulePriority = 2
        description  = "Keep last 30 tagged images"
        selection = {
          tagStatus     = "tagged"
          tagPrefixList = ["v", "release", "prod", "staging", "dev"]
          countType     = "imageCountMoreThan"
          countNumber   = 30
        }
        action = {
          type = "expire"
        }
      }
    ]
  })
}

# Kubernetes provider configuration uses the EKS cluster endpoint and token.
# This allows Terraform to apply the Kubernetes manifests (namespace, deployment, service).

provider "kubernetes" {
  host                   = module.eks.cluster_endpoint
  cluster_ca_certificate = base64decode(module.eks.cluster_certificate_authority_data)

  exec {
    api_version = "client.authentication.k8s.io/v1beta1"
    command     = "aws"
    args        = ["eks", "get-token", "--cluster-name", module.eks.cluster_name]
  }
}

resource "kubernetes_namespace_v1" "notes" {
  metadata {
    name = "notes-webapp"
  }
}

resource "kubernetes_deployment_v1" "notes" {
  metadata {
    name      = "notes-website-deployment"
    namespace = kubernetes_namespace_v1.notes.metadata[0].name
    labels = {
      app = "notes-webapp"
    }
  }

  spec {
    replicas = var.app_replicas

    selector {
      match_labels = {
        app = "notes-webapp"
      }
    }

    template {
      metadata {
        labels = {
          app = "notes-webapp"
        }
      }

      spec {
        container {
          name  = "notes-webapp"
          image = var.app_image

          port {
            container_port = var.app_container_port
          }

          resources {
            requests = {
              cpu    = "50m"
              memory = "64Mi"
            }
            limits = {
              cpu    = "250m"
              memory = "256Mi"
            }
          }

          liveness_probe {
            http_get {
              path = "/"
              port = var.app_container_port
            }
            initial_delay_seconds = 10
            period_seconds        = 10
          }

          readiness_probe {
            http_get {
              path = "/"
              port = var.app_container_port
            }
            initial_delay_seconds = 5
            period_seconds        = 5
          }
        }
      }
    }
  }

  depends_on = [module.eks]
}

resource "kubernetes_service_v1" "notes" {
  metadata {
    name      = "notes-website-service"
    namespace = kubernetes_namespace_v1.notes.metadata[0].name

    annotations = {
      # NOTE: On AWS, Service type LoadBalancer typically provisions an NLB.
      # The "alb" annotation is not standard for Services; ALB is usually via Ingress + AWS Load Balancer Controller.
      # We keep the annotation to match the repo, but recommend migrating to Ingress.
      "service.beta.kubernetes.io/aws-load-balancer-type"             = "alb"
      "service.beta.kubernetes.io/aws-load-balancer-backend-protocol" = "http"
    }
  }

  spec {
    selector = {
      app = "notes-webapp"
    }

    port {
      port        = var.app_service_port
      target_port = var.app_container_port
      protocol    = "TCP"
    }

    type = "LoadBalancer"
  }

  depends_on = [kubernetes_deployment_v1.notes]
}

output "vpc_id" {
  value       = module.vpc.vpc_id
  description = "VPC ID"
}

output "eks_cluster_name" {
  value       = module.eks.cluster_name
  description = "EKS cluster name"
}

output "eks_cluster_endpoint" {
  value       = module.eks.cluster_endpoint
  description = "EKS cluster endpoint"
}

output "ecr_repository_url" {
  value       = aws_ecr_repository.app.repository_url
  description = "ECR repository URL (use this in CI to push images)"
}

output "k8s_namespace" {
  value       = kubernetes_namespace_v1.notes.metadata[0].name
  description = "Kubernetes namespace"
}

output "service_name" {
  value       = kubernetes_service_v1.notes.metadata[0].name
  description = "Kubernetes service name"
}
