files.content:
terraform {
  required_version = ">= 1.5.0"

  required_providers {
    aws = {
      source  = "hashicorp/aws"
      version = "~> 5.0"
    }
    kubernetes = {
      source  = "hashicorp/kubernetes"
      version = "~> 2.25"
    }
  }
}

provider "aws" {
  region = var.aws_region

  default_tags {
    tags = var.tags
  }
}

# Kubernetes provider is configured from the EKS cluster created in this stack.
# This allows Terraform to create the Namespace/ServiceAccount and the Pod Identity association.
provider "kubernetes" {
  host                   = module.eks.cluster_endpoint
  cluster_ca_certificate = base64decode(module.eks.cluster_certificate_authority_data)

  exec {
    api_version = "client.authentication.k8s.io/v1beta1"
    command     = "aws"
    args        = ["eks", "get-token", "--cluster-name", module.eks.cluster_name, "--region", var.aws_region]
  }
}

variable "aws_region" {
  description = "AWS region to deploy into."
  type        = string
  default     = "us-east-1"
}

variable "name" {
  description = "Base name/prefix for resources."
  type        = string
  default     = "epicac"
}

variable "tags" {
  description = "Tags applied to all taggable resources."
  type        = map(string)
  default = {
    Project = "epicac"
    Managed = "terraform"
  }
}

variable "vpc_cidr" {
  description = "CIDR block for the VPC."
  type        = string
  default     = "10.0.0.0/16"
}

variable "az_count" {
  description = "Number of AZs to use (2 or 3 recommended)."
  type        = number
  default     = 2
}

variable "kubernetes_version" {
  description = "EKS Kubernetes version."
  type        = string
  default     = "1.29"
}

variable "node_instance_types" {
  description = "Instance types for the managed node group."
  type        = list(string)
  default     = ["t3.medium"]
}

variable "node_desired_size" {
  description = "Desired node count."
  type        = number
  default     = 2
}

variable "node_min_size" {
  description = "Minimum node count."
  type        = number
  default     = 1
}

variable "node_max_size" {
  description = "Maximum node count."
  type        = number
  default     = 3
}

variable "namespace" {
  description = "Kubernetes namespace for the workload."
  type        = string
  default     = "epicac"
}

variable "service_account_name" {
  description = "Kubernetes service account name used for EKS Pod Identity."
  type        = string
  default     = "epicac"
}

variable "ecr_repository_name" {
  description = "ECR repository name for the container image."
  type        = string
  default     = "epicac"
}

variable "container_image" {
  description = "Full container image reference (including tag) to run in the Pod. Example: <account>.dkr.ecr.<region>.amazonaws.com/epicac:latest"
  type        = string
}

variable "assume_role_arns" {
  description = "List of IAM role ARNs that the pod identity role is allowed to assume (role chaining / cross-account)."
  type        = list(string)
  default     = []
}

data "aws_availability_zones" "available" {
  state = "available"
}

locals {
  azs = slice(data.aws_availability_zones.available.names, 0, var.az_count)

  # /20s give room for growth; adjust if you need more IPs.
  private_subnets = [for i, az in local.azs : cidrsubnet(var.vpc_cidr, 4, i)]
  public_subnets  = [for i, az in local.azs : cidrsubnet(var.vpc_cidr, 4, i + 8)]

  cluster_name = "${var.name}-eks"
}

module "vpc" {
  source  = "terraform-aws-modules/vpc/aws"
  version = "~> 5.0"

  name = "${var.name}-vpc"
  cidr = var.vpc_cidr

  azs             = local.azs
  private_subnets = local.private_subnets
  public_subnets  = local.public_subnets

  enable_nat_gateway     = true
  single_nat_gateway     = true
  one_nat_gateway_per_az = false

  enable_dns_hostnames = true
  enable_dns_support   = true

  # Required for EKS
  public_subnet_tags = {
    "kubernetes.io/role/elb" = "1"
  }
  private_subnet_tags = {
    "kubernetes.io/role/internal-elb" = "1"
  }

  tags = var.tags
}

module "eks" {
  source  = "terraform-aws-modules/eks/aws"
  version = "~> 20.0"

  cluster_name    = local.cluster_name
  cluster_version = var.kubernetes_version

  vpc_id     = module.vpc.vpc_id
  subnet_ids = module.vpc.private_subnets

  cluster_endpoint_public_access  = true
  cluster_endpoint_private_access = true

  enable_cluster_creator_admin_permissions = true

  eks_managed_node_groups = {
    default = {
      name            = "${var.name}-ng"
      instance_types  = var.node_instance_types
      min_size        = var.node_min_size
      max_size        = var.node_max_size
      desired_size    = var.node_desired_size
      subnet_ids      = module.vpc.private_subnets

      ami_type = "AL2_x86_64"

      # Basic hardening
      metadata_options = {
        http_endpoint               = "enabled"
        http_tokens                 = "required"
        http_put_response_hop_limit = 2
      }
    }
  }

  # Add-ons (core)
  cluster_addons = {
    coredns = {
      most_recent = true
    }
    kube-proxy = {
      most_recent = true
    }
    vpc-cni = {
      most_recent = true
    }
    eks-pod-identity-agent = {
      most_recent = true
    }
  }

  tags = var.tags
}

# ECR repository for the container image
resource "aws_ecr_repository" "this" {
  name                 = var.ecr_repository_name
  image_tag_mutability = "MUTABLE"

  image_scanning_configuration {
    scan_on_push = true
  }

  encryption_configuration {
    encryption_type = "AES256"
  }

  tags = var.tags
}

resource "aws_ecr_lifecycle_policy" "this" {
  repository = aws_ecr_repository.this.name

  policy = jsonencode({
    rules = [
      {
        rulePriority = 1
        description  = "Expire untagged images older than 14 days"
        selection = {
          tagStatus   = "untagged"
          countType   = "sinceImagePushed"
          countUnit   = "days"
          countNumber = 14
        }
        action = {
          type = "expire"
        }
      },
      {
        rulePriority = 2
        description  = "Keep last 50 tagged images"
        selection = {
          tagStatus     = "tagged"
          tagPrefixList = ["v", "sha-", "latest"]
          countType     = "imageCountMoreThan"
          countNumber   = 50
        }
        action = {
          type = "expire"
        }
      }
    ]
  })
}

# IAM role used by EKS Pod Identity for the ServiceAccount
resource "aws_iam_role" "pod_identity" {
  name = "${var.name}-pod-identity"

  assume_role_policy = jsonencode({
    Version = "2012-10-17"
    Statement = [
      {
        Effect = "Allow"
        Principal = {
          Service = "pods.eks.amazonaws.com"
        }
        Action = [
          "sts:AssumeRole",
          "sts:TagSession"
        ]
      }
    ]
  })

  tags = var.tags
}

resource "aws_iam_policy" "pod_identity" {
  name        = "${var.name}-pod-identity"
  description = "Permissions for epicac pod identity role (assume target roles)."

  policy = jsonencode({
    Version = "2012-10-17"
    Statement = concat(
      [
        {
          Sid    = "AllowAssumeRole"
          Effect = "Allow"
          Action = [
            "sts:AssumeRole",
            "sts:AssumeRoleWithWebIdentity"
          ]
          Resource = var.assume_role_arns
        }
      ],
      length(var.assume_role_arns) == 0 ? [
        {
          Sid      = "DenyAssumeRoleWhenUnset"
          Effect   = "Deny"
          Action   = ["sts:AssumeRole", "sts:AssumeRoleWithWebIdentity"]
          Resource = "*"
        }
      ] : []
    )
  })
}

resource "aws_iam_role_policy_attachment" "pod_identity" {
  role       = aws_iam_role.pod_identity.name
  policy_arn = aws_iam_policy.pod_identity.arn
}

# Kubernetes namespace and service account
resource "kubernetes_namespace" "this" {
  metadata {
    name = var.namespace
    labels = {
      "app.kubernetes.io/name" = var.name
    }
  }

  depends_on = [module.eks]
}

resource "kubernetes_service_account" "this" {
  metadata {
    name      = var.service_account_name
    namespace = kubernetes_namespace.this.metadata[0].name
    labels = {
      "app.kubernetes.io/name" = var.name
    }
  }

  automount_service_account_token = true
}

# EKS Pod Identity association (ServiceAccount -> IAM Role)
resource "aws_eks_pod_identity_association" "this" {
  cluster_name    = module.eks.cluster_name
  namespace       = kubernetes_namespace.this.metadata[0].name
  service_account = kubernetes_service_account.this.metadata[0].name
  role_arn        = aws_iam_role.pod_identity.arn

  tags = var.tags
}

# Minimal Pod to run the container image.
# Note: for production, prefer a Deployment or Job; this mirrors your repo's pod.yaml intent.
resource "kubernetes_pod" "epicac" {
  metadata {
    name      = var.name
    namespace = kubernetes_namespace.this.metadata[0].name
    labels = {
      "app.kubernetes.io/name" = var.name
    }
  }

  spec {
    service_account_name = kubernetes_service_account.this.metadata[0].name

    container {
      name  = var.name
      image = var.container_image

      image_pull_policy = "IfNotPresent"

      # Keep it simple; your image contains both the Rust binary and Python runtime.
      command = ["/bin/sh", "-lc"]
      args    = ["sleep 3600"]

      resources {
        limits = {
          cpu    = "500m"
          memory = "512Mi"
        }
        requests = {
          cpu    = "100m"
          memory = "128Mi"
        }
      }

      security_context {
        allow_privilege_escalation = false
        read_only_root_filesystem  = false
        run_as_non_root            = true
      }
    }

    # Basic pod hardening
    security_context {
      fs_group = 1000
    }
  }

  depends_on = [aws_eks_pod_identity_association.this]
}

output "vpc_id" {
  value       = module.vpc.vpc_id
  description = "VPC ID"
}

output "eks_cluster_name" {
  value       = module.eks.cluster_name
  description = "EKS cluster name"
}

output "ecr_repository_url" {
  value       = aws_ecr_repository.this.repository_url
  description = "ECR repository URL"
}

output "pod_identity_role_arn" {
  value       = aws_iam_role.pod_identity.arn
  description = "IAM role ARN used by EKS Pod Identity"
}
