plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

title High-level Deployment Architecture (Node.js Express on AWS ECS)

skinparam shadowing false
skinparam componentStyle rectangle

actor "End User" as user

cloud "AWS Cloud" as aws {
  node "VPC (abstract)" as vpc {
    node "ECS Cluster" as ecs {
      node "ECS Service" as svc {
        artifact "ECS Task Definition" as taskdef
        node "ECS Task (Fargate/EC2)" as task {
          component "Container: hello-world\nNode.js 14 + Express.js\nListens on :3000" as app
        }
      }
    }

    database "CloudWatch Logs\nLog Group: /ecs/hello-world" as cwlogs
  }

  artifact "Amazon ECR\nRepository: ${ECR_REPO_NAME}" as ecr
}

node "GitHub" as github {
  component "GitHub Actions CI/CD\n- Build Docker image\n- Push to ECR\n- terraform init/apply" as gha
}

artifact "Terraform (IaC)\nState/Config (not in repo)" as tf

user --> app : HTTP request
app --> cwlogs : awslogs driver\nstdout/stderr

gha --> ecr : docker push\n(image: ${AWS_ACCOUNT_ID}.dkr.ecr.${AWS_REGION}.amazonaws.com/...)
svc --> ecr : pull image

gha --> tf : terraform init/plan/apply\n(provisions ECS/ECR/Logs)

@enduml
