plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

skinparam shadowing false
skinparam componentStyle rectangle

title Jump Game (Static Web) - High-Level Deployment Architecture

actor "End User" as user

cloud "Internet" as internet

node "CI/CD" as cicd {
  node "GitHub Actions Runner\n(Workflow: docker-image.yml)" as gha {
    artifact "Source Repo\n(HTML/CSS/JS)" as repo
    component "Docker Build & Push\n(Docker CLI)" as gha_docker
  }

  node "Jenkins Controller/Agent\n(Pipeline: Jenkinsfile)" as jenkins {
    component "Build & Push Image\n(Docker CLI)" as j_docker
    component "Security Scans\nTrivy / Docker Scout" as scans
    component "SCA\nOWASP Dependency-Check" as owasp
    component "Code Quality\nSonarQube Scanner" as sonar_scan
    component "Deploy\n(kubectl apply)" as kubectl
    component "IaC (optional)\nTerraform (EKS)" as tf
  }

  node "Quality/Security Services" as qss {
    database "SonarQube Server" as sonarqube
  }
}

cloud "Docker Hub" as dockerhub {
  artifact "Container Registry\n(yash5090/box-game:latest)" as registry
}

node "AWS" as aws {
  node "Kubernetes Cluster\n(Amazon EKS - implied)" as eks {
    node "K8s Namespace" as ns {
      node "Deployment: box-deployment\n(2 replicas)" as deploy {
        node "Pod (replica)" as pod1 {
          component "Nginx (alpine)\nserves static Jump Game" as nginx1
        }
        node "Pod (replica)" as pod2 {
          component "Nginx (alpine)\nserves static Jump Game" as nginx2
        }
      }

      node "Service: box-service\nType: LoadBalancer\nPort 5000 -> container" as svc
    }
  }

  node "AWS Load Balancer\n(ALB via annotation)" as alb
}

' Runtime traffic
user --> internet
internet --> alb : HTTPS/HTTP
alb --> svc : forwards
svc --> nginx1 : routes
svc --> nginx2 : routes

' CI/CD flows
repo --> gha_docker
repo --> j_docker

gha_docker --> registry : push image
j_docker --> registry : push image

scans --> registry : scan image
owasp --> repo : scan deps
sonar_scan --> sonarqube : publish analysis

kubectl --> eks : apply manifests\n(Deployment/Service)
tf --> aws : provision infra\n(optional)

registry --> deploy : image pull

@enduml
