terraform {
  required_version = ">= 1.6.0"

  required_providers {
    aws = {
      source  = "hashicorp/aws"
      version = "~> 5.0"
    }
  }
}

provider "aws" {
  region = var.aws_region

  default_tags {
    tags = merge(
      {
        Project     = var.project_name
        Environment = var.environment
        ManagedBy   = "terraform"
      },
      var.tags
    )
  }
}

variable "project_name" {
  description = "Project name used for resource naming."
  type        = string
  default     = "jump-game"
}

variable "environment" {
  description = "Environment name (e.g., dev, staging, prod)."
  type        = string
  default     = "dev"
}

variable "aws_region" {
  description = "AWS region."
  type        = string
  default     = "us-east-1"
}

variable "tags" {
  description = "Additional tags to apply to all resources."
  type        = map(string)
  default     = {}
}

variable "vpc_cidr" {
  description = "CIDR block for the VPC."
  type        = string
  default     = "10.0.0.0/16"
}

variable "az_count" {
  description = "Number of AZs to use (2 recommended)."
  type        = number
  default     = 2
}

variable "cluster_version" {
  description = "EKS Kubernetes version."
  type        = string
  default     = "1.29"
}

variable "node_instance_types" {
  description = "Instance types for the EKS managed node group."
  type        = list(string)
  default     = ["t3.medium"]
}

variable "node_desired_size" {
  description = "Desired number of worker nodes."
  type        = number
  default     = 2
}

variable "node_min_size" {
  description = "Minimum number of worker nodes."
  type        = number
  default     = 1
}

variable "node_max_size" {
  description = "Maximum number of worker nodes."
  type        = number
  default     = 3
}

variable "app_namespace" {
  description = "Kubernetes namespace for the application."
  type        = string
  default     = "box-game"
}

variable "app_image" {
  description = "Container image for the app (currently Docker Hub)."
  type        = string
  default     = "yash5090/box-game:latest"
}

variable "app_replicas" {
  description = "Number of application replicas."
  type        = number
  default     = 2
}

variable "app_container_port" {
  description = "Container port the app listens on (matches repo manifests)."
  type        = number
  default     = 5000
}

variable "service_port" {
  description = "Service port exposed by the Kubernetes Service."
  type        = number
  default     = 5000
}

locals {
  name_prefix = "${var.project_name}-${var.environment}"
}

data "aws_availability_zones" "available" {
  state = "available"
}

locals {
  azs = slice(data.aws_availability_zones.available.names, 0, var.az_count)

  # Derive /24s from the VPC /16 by default.
  public_subnet_cidrs  = [for i, _ in local.azs : cidrsubnet(var.vpc_cidr, 8, i)]
  private_subnet_cidrs = [for i, _ in local.azs : cidrsubnet(var.vpc_cidr, 8, i + 10)]
}

module "vpc" {
  source  = "terraform-aws-modules/vpc/aws"
  version = "~> 5.0"

  name = "${local.name_prefix}-vpc"
  cidr = var.vpc_cidr

  azs             = local.azs
  public_subnets  = local.public_subnet_cidrs
  private_subnets = local.private_subnet_cidrs

  enable_nat_gateway     = true
  single_nat_gateway     = true
  one_nat_gateway_per_az = false

  enable_dns_hostnames = true
  enable_dns_support   = true

  public_subnet_tags = {
    "kubernetes.io/role/elb" = "1"
  }

  private_subnet_tags = {
    "kubernetes.io/role/internal-elb" = "1"
  }
}

module "eks" {
  source  = "terraform-aws-modules/eks/aws"
  version = "~> 20.0"

  cluster_name    = "${local.name_prefix}-eks"
  cluster_version = var.cluster_version

  vpc_id     = module.vpc.vpc_id
  subnet_ids = module.vpc.private_subnets

  cluster_endpoint_public_access  = true
  cluster_endpoint_private_access = true

  enable_cluster_creator_admin_permissions = true

  cluster_enabled_log_types = [
    "api",
    "audit",
    "authenticator",
    "controllerManager",
    "scheduler"
  ]

  eks_managed_node_groups = {
    default = {
      name            = "${local.name_prefix}-ng"
      instance_types  = var.node_instance_types
      desired_size    = var.node_desired_size
      min_size        = var.node_min_size
      max_size        = var.node_max_size
      subnet_ids      = module.vpc.private_subnets
      capacity_type   = "ON_DEMAND"
      ami_type        = "AL2_x86_64"
      disk_size       = 20

      # Allow nodes to reach the internet via NAT for pulling images from Docker Hub.
      # (If you later move to ECR + VPC endpoints, you can tighten egress.)
    }
  }

  # Core add-ons
  cluster_addons = {
    coredns = {
      most_recent = true
    }
    kube-proxy = {
      most_recent = true
    }
    vpc-cni = {
      most_recent = true
    }
  }
}

# Kubernetes provider is configured from the EKS module outputs.
# This allows Terraform to apply Kubernetes manifests after the cluster is created.

provider "kubernetes" {
  host                   = module.eks.cluster_endpoint
  cluster_ca_certificate = base64decode(module.eks.cluster_certificate_authority_data)

  exec {
    api_version = "client.authentication.k8s.io/v1beta1"
    command     = "aws"
    args        = ["eks", "get-token", "--cluster-name", module.eks.cluster_name, "--region", var.aws_region]
  }
}

resource "kubernetes_namespace_v1" "app" {
  metadata {
    name = var.app_namespace
  }
}

resource "kubernetes_deployment_v1" "app" {
  metadata {
    name      = "box-deployment"
    namespace = kubernetes_namespace_v1.app.metadata[0].name
    labels = {
      app = "box-game"
    }
  }

  spec {
    replicas = var.app_replicas

    selector {
      match_labels = {
        app = "box-game"
      }
    }

    template {
      metadata {
        labels = {
          app = "box-game"
        }
      }

      spec {
        container {
          name  = "box-game"
          image = var.app_image

          port {
            container_port = var.app_container_port
          }

          resources {
            requests = {
              cpu    = "50m"
              memory = "64Mi"
            }
            limits = {
              cpu    = "250m"
              memory = "256Mi"
            }
          }

          liveness_probe {
            http_get {
              path = "/"
              port = var.app_container_port
            }
            initial_delay_seconds = 10
            period_seconds        = 10
          }

          readiness_probe {
            http_get {
              path = "/"
              port = var.app_container_port
            }
            initial_delay_seconds = 5
            period_seconds        = 5
          }
        }
      }
    }
  }

  depends_on = [module.eks]
}

resource "kubernetes_service_v1" "app" {
  metadata {
    name      = "box-service"
    namespace = kubernetes_namespace_v1.app.metadata[0].name

    # NOTE: Your repo uses an ALB annotation on a Service, but ALB is typically
    # provisioned via AWS Load Balancer Controller + Ingress.
    # For a minimal working setup without extra controllers, we use the default
    # Service type LoadBalancer (creates an NLB/CLB depending on EKS settings).
  }

  spec {
    selector = {
      app = "box-game"
    }

    port {
      port        = var.service_port
      target_port = var.app_container_port
      protocol    = "TCP"
    }

    type = "LoadBalancer"
  }

  depends_on = [kubernetes_deployment_v1.app]
}

output "vpc_id" {
  description = "VPC ID."
  value       = module.vpc.vpc_id
}

output "eks_cluster_name" {
  description = "EKS cluster name."
  value       = module.eks.cluster_name
}

output "eks_cluster_endpoint" {
  description = "EKS cluster API server endpoint."
  value       = module.eks.cluster_endpoint
}

output "app_namespace" {
  description = "Kubernetes namespace where the app is deployed."
  value       = kubernetes_namespace_v1.app.metadata[0].name
}

output "service_hostname" {
  description = "External hostname of the Kubernetes LoadBalancer service (when provisioned)."
  value       = try(kubernetes_service_v1.app.status[0].load_balancer[0].ingress[0].hostname, null)
}
