plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

title awsS3BucketList - High-level Deployment Architecture

skinparam shadowing false
skinparam componentStyle rectangle

node "Developer Workstation" as dev {
  artifact "awsS3BucketList\nGo CLI binary" as cli
  node "Dev Container (Docker)\nawscli, aws-vault, terraform" as devc
  artifact "AWS Credentials\n(~/.aws, env, aws-vault)" as creds
}

cloud "AWS Cloud" as aws {
  node "AWS Account" as acct {
    database "AWS STS\n(AssumeRole/GetCallerIdentity)" as sts
    storage "Amazon S3\n(ListBuckets)" as s3
  }
}

cli --> creds : loads default config
cli --> sts : auth/role session (optional)
cli --> s3 : ListBuckets API

@enduml
