plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

skinparam shadowing false
skinparam componentStyle rectangle

title High-level Deployment Architecture (S3-triggered Lambda + Litecoin Node Image CI/CD)

' AWS
node "AWS Account" as aws {
  node "AWS Region" as region {

    node "Amazon S3 Bucket\n(Object storage)" as s3 {
      artifact "Prefixes\n- source/\n- destination/" as prefixes
    }

    node "AWS Lambda Function\n(Python runtime)" as lambda {
      component "Handler: lambda_function.py\nTech: Python + boto3" as handler
      component "IAM Role\nPermissions:\n- s3:GetObject\n- s3:CopyObject\n- s3:DeleteObject" as iam
    }

  }
}

' CI/CD + Container image build
node "GitHub" as github {
  component "GitHub Actions Workflow\n(.github/workflows/dockerfile-ci.yml)" as gha
}

node "Docker Hub" as dockerhub {
  artifact "Container Image\nLitecoin node (litecoind)\nBuilt from Debian slim\nLitecoin v0.21.3" as image
}

node "Runtime Environment\n(Any container host)" as runtime {
  node "Litecoin Node Container" as litecoin {
    component "litecoind\nPorts: 9332 (RPC), 9333 (P2P)" as litecoind
  }
}

' Flows
s3 --> lambda : S3 ObjectCreated event\n(source/)
lambda --> s3 : Copy to destination/\nthen delete original

github --> gha
gha --> dockerhub : Build & push image\n(DOCKERHUB_USERNAME/TOKEN)
dockerhub --> runtime : Pull image
runtime --> litecoin

@enduml
