plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

title Terraform Tooling Container + CI Workflow (High-level Deployment)

skinparam shadowing false
skinparam componentStyle rectangle

actor "Developer" as dev

node "GitHub" as github {
  artifact "Source Repo\n(Dockerfile, docker-compose.yml,\nGitHub Actions workflow, tf-migrate)" as repo
  node "GitHub Actions Runner\n(Ubuntu VM)" as runner {
    component "Workflow: check-tf-version.yml\n(uses curl + jq)" as gha
    component "Docker Buildx\n(docker/setup-buildx-action)" as buildx
    artifact "Container Image\ncustom-alpine-terraform\n(Alpine + Terraform CLI + tf-migrate)" as image
  }
}

node "Local Machine" as local {
  component "Docker / Docker Compose\n(container runtime)" as docker
  component "Runs tooling container\n(Terraform CLI + tf-migrate)" as localrun
}

cloud "HashiCorp Services" as hashicorp {
  component "Checkpoint API\nhttps://checkpoint-api.hashicorp.com" as checkpoint
  component "Terraform Releases\nhttps://releases.hashicorp.com" as releases
}

' Relationships

dev --> repo : git push / PR
repo --> gha : triggers workflow

gha --> checkpoint : GET latest Terraform version
buildx --> releases : download Terraform CLI\n(TERRAFORM_VERSION)
buildx --> image : build container image

docker --> localrun : docker compose up / docker run
localrun --> image : pulls/uses image
localrun --> releases : (optional) download during build\n(if building locally)

@enduml
