plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

title VAT Calculator Static Site on AWS EKS (High-level Deployment)

skinparam shadowing false
skinparam componentStyle rectangle

actor "End User" as user

cloud "Internet" as internet

node "AWS" as aws {
  node "Amazon EKS Cluster" as eks {
    node "Kubernetes: vat-calc namespace" as k8s {
      node "Deployment: vat-calc\nreplicas=6" as deploy {
        artifact "Container Image\nnginx:alpine + static HTML/CSS/JS\n(DockerHub: yash5090/vat-calc:latest)" as image
        node "Pod (x6)" as pods {
          component "Nginx Web Server\n(serves static site)" as nginx
        }
      }

      node "Service: LoadBalancer\n(annotations: aws-load-balancer-type=alb)" as svc
    }
  }

  node "AWS Load Balancer (ALB)\n(provisioned via Service integration)" as alb
}

node "CI/CD" as cicd {
  component "GitHub Actions\n(docker-image.yml)" as gha
  component "Jenkins\n(Jenkinsfile)" as jenkins
  component "SonarQube" as sonarqube
  component "OWASP Dependency-Check" as owasp
  component "Trivy" as trivy
  component "Docker Scout" as scout
  database "DockerHub Registry" as dockerhub
}

node "IaC" as iac {
  component "Terraform\n(AWS provider ~>5.0)\n(EKS-Terraform/)" as tf
}

user --> internet
internet --> alb : HTTPS
alb --> svc : forwards
svc --> nginx : routes to pods
nginx --> image : runs

gha --> dockerhub : build & push image
jenkins --> sonarqube : code quality scan
jenkins --> owasp : dependency scan
jenkins --> trivy : image scan
jenkins --> scout : image insights
jenkins --> dockerhub : build & push image

tf --> eks : provision/maintain (implied)

@enduml
