plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

title Magic Color - High-level Deployment Architecture (EKS + ALB)

skinparam shadowing false
skinparam componentStyle rectangle
skinparam wrapWidth 220
skinparam maxMessageSize 120

actor "End User" as user

cloud "Internet" as internet

node "AWS Account" as aws {
  node "Amazon EKS Cluster" as eks {
    node "Kubernetes Namespace" as ns {
      node "Deployment: magic-color-deployment\nReplicas: N\nContainer: nginx:alpine\nStatic HTML/CSS/JS" as deploy {
        artifact "Container Image\n(yash5090/magic-color:latest\nor GHCR)" as image
      }
      node "Service: magic-color-service\nType: LoadBalancer\n(ALB annotations)" as svc
    }
  }

  node "AWS Load Balancer\n(ALB via Kubernetes Service)" as alb
}

node "Container Registry" as registry {
  artifact "Docker Hub\nRepo: yash5090/magic-color" as dockerhub
  artifact "GitHub Container Registry (GHCR)" as ghcr
}

node "CI/CD" as cicd {
  node "GitHub Actions\n(docker-image.yml, docker-publish.yml)" as gha
  node "Jenkins\n(Jenkinsfile)" as jenkins
}

node "IaC" as iac {
  node "Terraform\n(AWS Provider ~> 5.x)" as tf
}

user --> internet
internet --> alb : HTTPS
alb --> svc : forwards
svc --> deploy : routes to Pods

gha --> registry : build/push/sign image
jenkins --> registry : build/push image
registry --> image : pull

tf --> eks : provision/maintain\n(EKS + networking)

@enduml
