plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

skinparam shadowing false
skinparam componentStyle rectangle
skinparam wrapWidth 220
skinparam maxMessageSize 120

title Deployment Architecture - Node.js (Express + Socket.IO) Chat Server with CI/CD

actor "End User" as user

node "Client Device" as client {
  artifact "Web Browser" as browser
}

cloud "Internet" as internet

node "Container Host" as host {
  node "Docker Engine" as docker {
    node "Chat Server Container" as app {
      artifact "Node.js App\nExpress + Socket.IO\nListens: TCP 12345" as nodeapp
      artifact "Static Assets\n/public" as public
    }
  }
}

node "CI/CD & IaC" as cicd {
  artifact "Jenkins Pipeline\n(build, OWASP dep check,\nDocker build/tag/push, trigger CD)" as jenkins
  artifact "AWS CodeBuild\n(buildspec.yml)\nBuild + Artifacts (/public)" as codebuild
  artifact "GitHub Actions\nterraform-plan/apply" as gha
  artifact "HCP Terraform / Terraform Cloud\nOrg + Workspace\nPlan/Apply Runs" as tfc
}

database "Container Registry\n(e.g., Docker Hub / ECR)" as registry

folder "Artifact Output" as artifacts {
  artifact "Build Artifacts\n/public" as build_artifacts
}

user --> browser
browser --> internet
internet --> host : WebSocket/HTTP\nTCP 12345

jenkins --> registry : docker push\nimage:tag
codebuild --> artifacts : publish artifacts
codebuild --> registry : (optional) docker push

gha --> tfc : trigger plan/apply

tfc --> host : provision/update infra\n(e.g., VM/SG/DNS)

registry --> docker : docker pull\nchat-server image

docker --> app : run container

@enduml
