plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

title Static Web App on Kubernetes (EKS-ready) with CI/CD

skinparam shadowing false
skinparam componentStyle rectangle

actor "End User" as user

cloud "Internet" as internet

node "AWS" as aws {
  node "Kubernetes Cluster\n(EKS or compatible)" as k8s {

    node "Ingress / External Load Balancer\n(Service: LoadBalancer + AWS ALB annotations)" as lb

    node "Namespace: web" as ns {
      node "Deployment: range-slider" as deploy {
        artifact "Container Image\nnginx:alpine + static HTML/CSS/JS" as image
        node "Pod(s)" as pods {
          component "Nginx\n(static web server)" as nginx
          component "Static Assets\nHTML/CSS/JavaScript" as assets
        }
      }

      node "Service: app-svc\n(type: LoadBalancer/ClusterIP)" as svc
    }
  }

  database "Container Registry\nDocker Hub: yash5090/range-slider:latest\n+ GHCR (ghcr.io)" as registry
}

node "CI/CD" as cicd {
  component "GitHub Actions\n(docker build/push/sign; jekyll/builder in CI)" as gha
  component "Jenkins Pipeline\n(Jenkinsfile)" as jenkins
}

user --> internet
internet --> lb : HTTPS
lb --> svc : forwards
svc --> pods : routes
nginx --> assets : serves
pods ..> image : runs
image ..> registry : pulled from

gha ..> registry : build & push
jenkins ..> registry : build & push

gha ..> k8s : deploy manifests\n(deployment.yaml/service.yaml)
jenkins ..> k8s : deploy manifests\n(manifests/*.yaml)

@enduml
