plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

title React SPA on Amazon EKS (CI/CD + ALB)

skinparam shadowing false
skinparam componentStyle rectangle

actor "End User" as user

cloud "Internet" as internet

node "GitHub" as github {
  artifact "Source Repo\n(React / CRA)" as repo
  artifact "GitHub Container Registry\n(GHCR)" as ghcr
  component "GitHub Actions\nBuild/Test\nDocker build+push" as gha
}

node "Jenkins" as jenkins {
  component "Jenkins Pipeline\n(SonarQube, Trivy,\nOWASP Dep-Check,\nDocker Scout, kubectl deploy)" as jpipe
}

node "AWS Account" as aws {
  node "Amazon EKS Cluster" as eks {
    node "Kubernetes Namespace" as ns {
      node "Deployment: react-spa\nreplicas=6" as deploy {
        node "Pod" as pod1 {
          component "React SPA Container\n(Nginx/Static server)\nImage: from GHCR/DockerHub" as app
        }
      }
      node "Service: LoadBalancer\n(ALB annotations)" as svc
    }
  }
  node "AWS Load Balancer\n(Application Load Balancer via\nAWS Load Balancer Controller)" as alb
}

user --> internet
internet --> alb : HTTPS
alb --> svc : forwards
svc --> deploy : routes to pods

repo --> gha : push/PR triggers
gha --> ghcr : push image

repo --> jenkins : webhook / polling
jpipe --> ghcr : pull image metadata
jpipe --> eks : kubectl apply\n(deployment.yaml/service.yaml)

@enduml
