plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

title High-Level Deployment Architecture (Static Web App on Kubernetes + CI/CD)

skinparam shadowing false
skinparam componentStyle rectangle

actor "End User" as user

cloud "Internet" as internet

node "AWS" as aws {
  node "Amazon EKS Cluster\n(Kubernetes)" as eks {
    node "K8s Namespace" as ns {
      node "Kubernetes Deployment\nReplicas: 6" as deploy {
        artifact "Nginx Container\n(nginx:alpine)\nServes: HTML/CSS/JS" as nginx
      }
      node "Kubernetes Service\nType: LoadBalancer\n(annotated for ALB)" as svc
    }
  }

  node "AWS Application Load Balancer (ALB)" as alb
}

node "Container Registry\n(Docker Hub / GHCR)" as registry

node "CI/CD" as cicd {
  node "Jenkins Pipeline" as jenkins
  node "GitHub Actions" as gha
}

user --> internet
internet --> alb : HTTPS
alb --> svc : forwards
svc --> nginx : routes to pods

jenkins --> registry : build/scan/push image
jenkins --> eks : kubectl apply / deploy

gha --> registry : build & publish image

registry --> nginx : image pull

@enduml
