plantuml_diagram:
@startuml
!define AWSPuml https://raw.githubusercontent.com/awslabs/aws-icons-for-plantuml/v20.0/dist
!include AWSPuml/AWSCommon.puml

title High-level Deployment Architecture (Node/Express app on EKS)

skinparam shadowing false
skinparam componentStyle rectangle
skinparam wrapWidth 220
skinparam maxMessageSize 120

actor "End User" as user

cloud "AWS" as aws {
  node "Amazon Route 53 (optional)" as r53
  node "AWS Load Balancer\n(ALB via K8s Service type=LoadBalancer)" as alb

  node "Amazon EKS Cluster" as eks {
    node "Kubernetes Namespace" as ns {
      node "Deployment: tribute\nreplicas: 6" as deploy {
        artifact "Container Image\nyash5090/tribute:latest\n(or GHCR)" as image
        node "Pod" as pod1 {
          component "Web App Container\nNode.js + Express.js\nserves static HTML" as app
        }
        node "Pod" as pod2 {
          component "Web App Container\nNode.js + Express.js\nserves static HTML" as app2
        }
        node "Pod" as podN {
          component "Web App Container\nNode.js + Express.js\nserves static HTML" as appN
        }
      }

      node "Service: tribute-svc\nType: LoadBalancer\nPort: 3000" as svc
    }
  }

  node "Container Registry" as registry {
    artifact "DockerHub\nyash5090/tribute" as dh
    artifact "GitHub Container Registry\n(GHCR)" as ghcr
  }
}

node "CI/CD" as cicd {
  node "GitHub Actions" as gha {
    component "Build & Publish\nDocker image" as gha_build
  }
  node "Jenkins" as jenkins {
    component "CI: SonarQube\nOWASP Dependency-Check\nTrivy\nDocker build/tag/push" as j_ci
    component "CD (Prod): kubectl apply\n(deployment.yaml/service.yaml)" as j_cd
  }
  node "Code Quality & Security" as tools {
    component "SonarQube" as sonar
    component "OWASP Dependency-Check" as owasp
    component "Trivy" as trivy
  }
}

node "IaC" as iac {
  component "Terraform (AWS Provider v5.x)\nEKS-Terraform/\n(infra intended; .tf not present)" as tf
}

' Traffic flow
user --> r53 : DNS lookup (optional)
r53 --> alb : resolves to ALB
user --> alb : HTTPS
alb --> svc : forwards to Service
svc --> app : routes to Pods (port 3000)
svc --> app2
svc --> appN

' Build & deploy flow
gha_build --> dh : push image
gha_build --> ghcr : push image
j_ci --> sonar
j_ci --> owasp
j_ci --> trivy
j_ci --> dh : push image
j_ci --> ghcr : push image
j_cd --> eks : kubectl apply manifests

' Runtime dependency
deploy ..> image : pulls
image ..> dh
image ..> ghcr

' IaC relationship
tf ..> eks : provisions/updates (intended)

note right of app
Dockerfile uses nginx:alpine to serve static content,
while manifests expose port 3000 and mention Node/Express.
Diagram models the app as Node/Express container as per analysis.
end note

@enduml
